import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import './UserProfilePage.css';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { 
  faArrowLeft, 
  faMapMarkerAlt, 
  faCalendar,
  faBaby,
  faEye,
  faEyeSlash,
  faArchive,
  faMars,
  faVenus
} from '@fortawesome/free-solid-svg-icons';

const UserProfilePage = () => {
  const { userId } = useParams();
  const navigate = useNavigate();
  const [user, setUser] = useState(null);
  const [posts, setPosts] = useState([]);
  const [children, setChildren] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [activeTab, setActiveTab] = useState('posts');

  useEffect(() => {
    fetchUserProfile();
    loadChildrenData();
  }, [userId]);

  const fetchUserProfile = async () => {
    try {
      setLoading(true);
      console.log('Загружаем профиль пользователя ID:', userId);
      
      // Получаем токен авторизации
      const token = localStorage.getItem('accessToken') || sessionStorage.getItem('accessToken');
      const headers = token ? { 'Authorization': `Bearer ${token}` } : {};
      
      // Получаем данные пользователя с постами и детьми
      const userResponse = await fetch(`http://localhost:8000/api/users/profile-with-posts/${userId}/`, {
        headers: {
          'Content-Type': 'application/json',
          ...headers
        }
      });
      
      if (userResponse.ok) {
        const userData = await userResponse.json();
        console.log('Данные пользователя:', userData);
        const user = userData.user || userData;
        setUser(user);
        
        // Сохраняем данные о детях
        if (user.children) {
          setChildren(user.children);
        }
      } else {
        console.error('Ошибка получения пользователя:', userResponse.status);
        setError(`Пользователь не найден ID пользователя: ${userId}`);
      }

      // Получаем только опубликованные посты пользователя
      const postsResponse = await fetch(`http://localhost:8000/api/posts/user/${userId}/published/`, {
        headers: {
          'Content-Type': 'application/json',
          ...headers
        }
      });
      
      if (postsResponse.ok) {
        const postsData = await postsResponse.json();
        console.log('Опубликованные посты пользователя:', postsData);
        setPosts(postsData.posts || []);
      } else {
        console.error('Ошибка получения постов:', postsResponse.status);
        setPosts([]);
      }
    } catch (error) {
      console.error('Ошибка загрузки профиля:', error);
      setError('Ошибка загрузки профиля пользователя');
      setPosts([]);
    } finally {
      setLoading(false);
    }
  };

  const loadChildrenData = () => {
    // Данные о детях теперь загружаются через API в fetchUserProfile
    // Эта функция оставлена для совместимости
  };

  const getAuthorInitials = (authorName) => {
    if (!authorName) return 'А';
    const names = authorName.split(' ');
    return `${names[0]?.charAt(0) || 'А'}${names[1]?.charAt(0) || ''}`.toUpperCase();
  };

  const formatDate = (dateString) => {
    if (!dateString) return 'Недавно';
    const date = new Date(dateString);
    const now = new Date();
    const diffInHours = Math.floor((now - date) / (1000 * 60 * 60));
    
    if (diffInHours < 1) return 'Только что';
    if (diffInHours < 24) return `${diffInHours} ч назад`;
    
    const diffInDays = Math.floor(diffInHours / 24);
    if (diffInDays < 7) return `${diffInDays} дн назад`;
    
    return date.toLocaleDateString('ru-RU');
  };

  const renderPosts = () => {
    if (posts.length === 0) {
      return (
        <div className="empty-posts">
          <div className="empty-icon">
            <FontAwesomeIcon icon={faEye} />
          </div>
          <h3>Постов пока нет</h3>
          <p>Пользователь еще не создал публикаций</p>
        </div>
      );
    }

    return (
      <div className="posts-grid">
        {posts.map((post) => (
          <div key={post.id} className="post-item">
            {/* Заголовок */}
            <div 
              className="post-title clickable-title"
              onClick={(e) => {
                console.log('Клик по заголовку поста:', post.title);
                e.stopPropagation();
                navigate(`/post-detail/${post.slug || post.id}`);
              }}
            >
              {post.title}
            </div>
            
            {/* Тег */}
            <div className="post-tag">
              {post.category?.name || 'Общее'}
            </div>
            
            {/* Информация об авторе */}
            <div className="post-author">
              <div className="author-avatar">
                {user?.avatar ? (
                  <img 
                    src={user.avatar} 
                    alt={user.first_name}
                    onError={(e) => {
                      e.target.style.display = 'none';
                      e.target.nextSibling.style.display = 'flex';
                    }}
                  />
                ) : null}
                <div className="author-initials" style={{ 
                  display: user?.avatar ? 'none' : 'flex' 
                }}>
                  {getAuthorInitials(user?.first_name || user?.username)}
                </div>
              </div>
              <div className="author-info">
                <span className="author-name">
                  {user?.first_name || user?.username || 'Пользователь'}
                </span>
                <span className="author-location">
                  <FontAwesomeIcon icon={faMapMarkerAlt} />
                  {user?.city || 'Не указан'}
                </span>
                <span className="post-date">
                  <FontAwesomeIcon icon={faCalendar} />
                  {formatDate(post.created_at)}
                </span>
              </div>
            </div>
            
            {/* Отрывок текста статьи */}
            {post.content && (
              <div className="post-excerpt">
                <p>{post.content.length > 67 ? `${post.content.substring(0, 67)}...` : post.content}</p>
              </div>
            )}
            
            {/* Кнопка "Читать дальше" */}
            <div className="post-read-more">
              <button 
                className="read-more-btn"
                onClick={(e) => {
                  e.stopPropagation();
                  navigate(`/post-detail/${post.slug || post.id}`);
                }}
              >
                Читать дальше
              </button>
            </div>
          </div>
        ))}
      </div>
    );
  };

  if (loading) {
    return (
      <div className="user-profile-loading">
        <div className="loading-spinner"></div>
        <p>Загрузка профиля...</p>
      </div>
    );
  }

  if (error || !user) {
    return (
      <div className="user-profile-error">
        <h2>Ошибка</h2>
        <p>{error || 'Пользователь не найден'}</p>
        <button onClick={() => navigate('/')} className="btn-back">
          <FontAwesomeIcon icon={faArrowLeft} />
          Вернуться на главную
        </button>
      </div>
    );
  }

  return (
    <div className="user-profile-page">
      <div className="user-profile-container">
        {/* Кнопка назад */}
        <div className="user-profile-header">
          <button onClick={() => navigate('/')} className="btn-back">
            <FontAwesomeIcon icon={faArrowLeft} />
            Назад
          </button>
        </div>

        {/* Секция профиля пользователя */}
        <div className="user-profile-section">
          <div className="user-avatar">
            {user.avatar ? (
              <img 
                src={user.avatar} 
                alt={user.first_name || user.username}
                onError={(e) => {
                  e.target.style.display = 'none';
                  e.target.nextSibling.style.display = 'flex';
                }}
              />
            ) : null}
            <div className="avatar-placeholder" style={{ 
              display: user.avatar ? 'none' : 'flex' 
            }}>
              {getAuthorInitials(user.first_name || user.username)}
            </div>
          </div>
          
          <div className="user-info">
            <h1 className="user-name">{user.first_name || user.username}</h1>
            <div className="user-details">
              <span>
                <FontAwesomeIcon icon={faMapMarkerAlt} />
                {user.city || 'Не указан'}
              </span>
              <span>
                <FontAwesomeIcon icon={faCalendar} />
                {formatDate(user.date_joined || user.created_at)}
              </span>
              <span>@{user.username}</span>
            </div>
          </div>
        </div>

        {/* Секция с детьми */}
        {children.length > 0 && (
          <div className="children-section">
            <div className="section-header">
              <FontAwesomeIcon icon={faBaby} />
              <h2>Дети</h2>
            </div>
            <div className="children-grid">
              {children.map((child) => (
                <div key={child.id} className="child-card">
                  <div className={`child-avatar ${child.gender === 'female' ? 'female' : 'male'}`}>
                    <FontAwesomeIcon icon={child.gender === 'female' ? faVenus : faMars} />
                  </div>
                  <div className="child-info">
                    <div className="child-name">{child.name}</div>
                    <div className="child-age">{child.age} {child.age === 1 ? 'год' : child.age < 5 ? 'года' : 'лет'}</div>
                  </div>
                </div>
              ))}
            </div>
          </div>
        )}

        {/* Статистика */}
        <div className="stats-section">
          <div className="stat-card">
            <div className="stat-number">{user?.published_posts_count || posts.length}</div>
            <div className="stat-label">постов</div>
          </div>
          <div className="stat-card">
            <div className="stat-number">{children.length}</div>
            <div className="stat-label">детей</div>
          </div>
          <div className="stat-card">
            <div className="stat-number">0</div>
            <div className="stat-label">подписчиков</div>
          </div>
          <div className="stat-card">
            <div className="stat-number">0</div>
            <div className="stat-label">подписок</div>
          </div>
        </div>

        {/* Табы */}
        <div className="tabs-section">
          <div className="tabs">
            <button 
              className={`tab ${activeTab === 'posts' ? 'active' : ''}`}
              onClick={() => setActiveTab('posts')}
            >
              Записи ({posts.length})
            </button>
          </div>
        </div>

        {/* Контент */}
        <div className="content-section">
          {activeTab === 'posts' && renderPosts()}
        </div>
      </div>
    </div>
  );
};

export default UserProfilePage;
