import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faSave, faEye, faEyeSlash, faUpload, faTimes } from '@fortawesome/free-solid-svg-icons';
import './CreatePost.css';

const CreatePost = () => {
  const navigate = useNavigate();
  const [categories, setCategories] = useState([]);
  const [loading, setLoading] = useState(false);
  const [formData, setFormData] = useState({
    title: '',
    short_description: '',
    content: '',
    category: '',
    status: 'draft',
    comments_enabled: 'enabled',
    meta_title: '',
    meta_description: '',
    meta_keywords: '',
    featured_image: null
  });
  const [errors, setErrors] = useState({});
  const [imagePreview, setImagePreview] = useState(null);

  useEffect(() => {
    fetchCategories();
  }, []);

  const fetchCategories = async () => {
    try {
      const response = await fetch('http://localhost:8000/api/posts/categories/');
      if (response.ok) {
        const data = await response.json();
        setCategories(data);
      } else {
        console.error('Ошибка загрузки категорий:', response.status);
      }
    } catch (error) {
      console.error('Ошибка загрузки категорий:', error);
      // Показываем сообщение пользователю
      alert('Не удалось загрузить категории. Убедитесь, что сервер запущен.');
    }
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
    
    // Очищаем ошибку для этого поля
    if (errors[name]) {
      setErrors(prev => ({
        ...prev,
        [name]: ''
      }));
    }
  };

  const handleImageChange = (e) => {
    const file = e.target.files[0];
    if (file) {
      setFormData(prev => ({
        ...prev,
        featured_image: file
      }));
      
      // Создаем превью
      const reader = new FileReader();
      reader.onload = (e) => {
        setImagePreview(e.target.result);
      };
      reader.readAsDataURL(file);
    }
  };

  const removeImage = () => {
    setFormData(prev => ({
      ...prev,
      featured_image: null
    }));
    setImagePreview(null);
  };

  const validateForm = () => {
    const newErrors = {};
    
    if (!formData.title.trim()) {
      newErrors.title = 'Заголовок обязателен';
    }
    
    if (!formData.short_description.trim()) {
      newErrors.short_description = 'Краткое описание обязательно';
    }
    
    if (!formData.content.trim()) {
      newErrors.content = 'Содержание обязательно';
    }
    
    if (!formData.category) {
      newErrors.category = 'Выберите категорию';
    }
    
    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    
    if (!validateForm()) {
      return;
    }
    
    setLoading(true);
    
    try {
      const token = localStorage.getItem('accessToken') || sessionStorage.getItem('accessToken');
      
      const formDataToSend = new FormData();
      formDataToSend.append('title', formData.title);
      formDataToSend.append('short_description', formData.short_description);
      formDataToSend.append('content', formData.content);
      formDataToSend.append('category', formData.category);
      formDataToSend.append('status', formData.status);
      formDataToSend.append('comments_enabled', formData.comments_enabled);
      formDataToSend.append('meta_title', formData.meta_title);
      formDataToSend.append('meta_description', formData.meta_description);
      formDataToSend.append('meta_keywords', formData.meta_keywords);
      
      if (formData.featured_image) {
        formDataToSend.append('featured_image', formData.featured_image);
      }
      
      const response = await fetch('http://localhost:8000/api/posts/create/', {
        method: 'POST',
        headers: {
          'Authorization': `Bearer ${token}`
        },
        body: formDataToSend
      });
      
      if (response.ok) {
        const data = await response.json();
        alert('Пост успешно создан!');
        navigate(`/post/${data.post.slug}`);
      } else {
        const errorData = await response.json();
        alert(`Ошибка: ${errorData.message || 'Не удалось создать пост'}`);
      }
    } catch (error) {
      console.error('Ошибка создания поста:', error);
      alert('Произошла ошибка при создании поста');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="create-post">
      <div className="create-post-container">
        <div className="create-post-header">
          <h1>Создать новый пост</h1>
          <p>Поделитесь своими мыслями и опытом с другими мамами</p>
        </div>
        <div style={{ padding: '20px', background: 'white', color: 'black' }}>
          <p>Компонент загружен! Если вы видите этот текст, значит компонент работает.</p>
        </div>
        
        <form onSubmit={handleSubmit} className="create-post-form">
          {/* Основная информация */}
          <div className="form-section">
            <h3>Основная информация</h3>
            
            <div className="form-group">
              <label htmlFor="title">Заголовок *</label>
              <input
                type="text"
                id="title"
                name="title"
                value={formData.title}
                onChange={handleInputChange}
                placeholder="Введите заголовок поста"
                className={errors.title ? 'error' : ''}
              />
              {errors.title && <span className="error-message">{errors.title}</span>}
            </div>
            
            <div className="form-group">
              <label htmlFor="short_description">Краткое описание (1-2 слова) *</label>
              <input
                type="text"
                id="short_description"
                name="short_description"
                value={formData.short_description}
                onChange={handleInputChange}
                placeholder="Например: Прикорм, Сон, Игры"
                className={errors.short_description ? 'error' : ''}
              />
              {errors.short_description && <span className="error-message">{errors.short_description}</span>}
            </div>
            
            <div className="form-group">
              <label htmlFor="category">Категория *</label>
              <select
                id="category"
                name="category"
                value={formData.category}
                onChange={handleInputChange}
                className={errors.category ? 'error' : ''}
              >
                <option value="">Выберите категорию</option>
                {categories.map(category => (
                  <option key={category.id} value={category.id}>
                    {category.name}
                  </option>
                ))}
              </select>
              {errors.category && <span className="error-message">{errors.category}</span>}
            </div>
            
            <div className="form-group">
              <label htmlFor="content">Содержание *</label>
              <textarea
                id="content"
                name="content"
                value={formData.content}
                onChange={handleInputChange}
                placeholder="Напишите содержание вашего поста..."
                rows="10"
                className={errors.content ? 'error' : ''}
              />
              {errors.content && <span className="error-message">{errors.content}</span>}
            </div>
          </div>
          
          {/* Настройки */}
          <div className="form-section">
            <h3>Настройки</h3>
            
            <div className="form-row">
              <div className="form-group">
                <label htmlFor="status">Статус публикации</label>
                <select
                  id="status"
                  name="status"
                  value={formData.status}
                  onChange={handleInputChange}
                >
                  <option value="draft">Черновик</option>
                  <option value="published">Опубликовать</option>
                </select>
              </div>
              
              <div className="form-group">
                <label htmlFor="comments_enabled">Комментарии</label>
                <select
                  id="comments_enabled"
                  name="comments_enabled"
                  value={formData.comments_enabled}
                  onChange={handleInputChange}
                >
                  <option value="enabled">Разрешены</option>
                  <option value="disabled">Запрещены</option>
                </select>
              </div>
            </div>
          </div>
          
          {/* Изображение */}
          <div className="form-section">
            <h3>Главное изображение</h3>
            
            <div className="image-upload">
              {imagePreview ? (
                <div className="image-preview">
                  <img src={imagePreview} alt="Превью" />
                  <button type="button" onClick={removeImage} className="remove-image">
                    <FontAwesomeIcon icon={faTimes} />
                  </button>
                </div>
              ) : (
                <div className="upload-area">
                  <input
                    type="file"
                    id="featured_image"
                    name="featured_image"
                    accept="image/*"
                    onChange={handleImageChange}
                    style={{ display: 'none' }}
                  />
                  <label htmlFor="featured_image" className="upload-label">
                    <FontAwesomeIcon icon={faUpload} />
                    <span>Загрузить изображение</span>
                  </label>
                </div>
              )}
            </div>
          </div>
          
          {/* SEO настройки */}
          <div className="form-section">
            <h3>SEO настройки</h3>
            
            <div className="form-group">
              <label htmlFor="meta_title">Meta Title</label>
              <input
                type="text"
                id="meta_title"
                name="meta_title"
                value={formData.meta_title}
                onChange={handleInputChange}
                placeholder="Заголовок для поисковых систем (до 60 символов)"
                maxLength="60"
              />
              <span className="char-count">{formData.meta_title.length}/60</span>
            </div>
            
            <div className="form-group">
              <label htmlFor="meta_description">Meta Description</label>
              <textarea
                id="meta_description"
                name="meta_description"
                value={formData.meta_description}
                onChange={handleInputChange}
                placeholder="Описание для поисковых систем (до 160 символов)"
                rows="3"
                maxLength="160"
              />
              <span className="char-count">{formData.meta_description.length}/160</span>
            </div>
            
            <div className="form-group">
              <label htmlFor="meta_keywords">Meta Keywords</label>
              <input
                type="text"
                id="meta_keywords"
                name="meta_keywords"
                value={formData.meta_keywords}
                onChange={handleInputChange}
                placeholder="Ключевые слова через запятую"
              />
            </div>
          </div>
          
          {/* Кнопки */}
          <div className="form-actions">
            <button type="submit" className="btn-save" disabled={loading}>
              <FontAwesomeIcon icon={faSave} />
              {loading ? 'Создание...' : 'Создать пост'}
            </button>
            
            <button type="button" className="btn-preview" onClick={() => navigate('/')}>
              <FontAwesomeIcon icon={faEye} />
              Отмена
            </button>
          </div>
        </form>
      </div>
    </div>
  );
};

export default CreatePost;
