// Утилита для работы с пользователями

// Тестовая функция для получения моковых данных пользователя
export const getMockUserInfo = (userId) => {
  console.log('Используем моковые данные для пользователя:', userId);
  
  // Создаем более реалистичные моковые данные
  const mockNames = [
    'Анна', 'Мария', 'Елена', 'Ольга', 'Татьяна', 'Наталья', 'Ирина', 'Светлана',
    'Александр', 'Дмитрий', 'Сергей', 'Андрей', 'Алексей', 'Максим', 'Владимир', 'Иван'
  ];
  
  const mockCities = [
    'Москва', 'Санкт-Петербург', 'Новосибирск', 'Екатеринбург', 'Казань', 'Нижний Новгород',
    'Челябинск', 'Самара', 'Ростов-на-Дону', 'Уфа', 'Красноярск', 'Пермь'
  ];
  
  // Генерируем имя на основе ID для консистентности
  const nameIndex = userId % mockNames.length;
  const cityIndex = userId % mockCities.length;
  
  return {
    id: userId,
    name: mockNames[nameIndex],
    username: `user_${userId}`,
    avatar: null,
    city: mockCities[cityIndex],
    isOnline: false
  };
};

// Функция для синхронизации данных между страницами
export const syncUserDataFromProfile = (userId, profileData) => {
  try {
    if (profileData && profileData.user) {
      const userInfo = {
        id: userId,
        name: profileData.user.first_name || profileData.user.username || `Пользователь ${userId}`,
        avatar: profileData.user.avatar || null,
        city: profileData.user.city || 'Не указан'
      };
      
      console.log('Синхронизируем данные пользователя из профиля:', userInfo);
      saveUserInfoToStorage(userInfo);
      return userInfo;
    }
  } catch (error) {
    console.error('Ошибка при синхронизации данных пользователя:', error);
  }
  return null;
};

// Функция для получения информации о пользователе из localStorage (если есть)
export const getUserInfoFromStorage = (userId) => {
  try {
    // Пытаемся найти информацию о пользователе в localStorage
    const storedUsers = localStorage.getItem('userProfiles');
    if (storedUsers) {
      const users = JSON.parse(storedUsers);
      const user = users.find(u => u.id == userId);
      if (user) {
        console.log('Найдена информация о пользователе в localStorage:', user);
        return {
          id: user.id,
          name: user.first_name || user.username || `Пользователь ${userId}`,
          avatar: user.avatar || null,
          city: user.city || 'Не указан'
        };
      }
    }
  } catch (error) {
    console.error('Ошибка при получении информации из localStorage:', error);
  }
  return null;
};

// Функция для сохранения информации о пользователе в localStorage
export const saveUserInfoToStorage = (userInfo) => {
  try {
    const storedUsers = localStorage.getItem('userProfiles') || '{}';
    const users = JSON.parse(storedUsers);
    users[userInfo.id] = userInfo;
    localStorage.setItem('userProfiles', JSON.stringify(users));
    console.log('Информация о пользователе сохранена в localStorage:', userInfo);
  } catch (error) {
    console.error('Ошибка при сохранении в localStorage:', error);
  }
};

// Получить информацию о пользователе по ID
export const getUserInfo = async (userId) => {
  try {
    console.log('Получаем информацию о пользователе:', userId);
    
    // Сначала проверяем localStorage
    const storedUserInfo = getUserInfoFromStorage(userId);
    if (storedUserInfo) {
      console.log('Используем информацию из localStorage:', storedUserInfo);
      return storedUserInfo;
    }
    
    // Для тестирования используем моковые данные
    if (process.env.NODE_ENV === 'development') {
      console.log('Режим разработки, используем моковые данные');
      const mockUserInfo = getMockUserInfo(userId);
      // Сохраняем моковые данные в localStorage для консистентности
      saveUserInfoToStorage(mockUserInfo);
      return mockUserInfo;
    }
    
    const token = localStorage.getItem('accessToken') || sessionStorage.getItem('accessToken');
    console.log('Токен найден:', !!token);
    
    if (!token) {
      console.log('Токен не найден, возвращаем базовую информацию');
      const basicUserInfo = {
        id: userId,
        name: `Пользователь ${userId}`,
        avatar: null,
        city: 'Не указан'
      };
      saveUserInfoToStorage(basicUserInfo);
      return basicUserInfo;
    }

    console.log('Делаем API-запрос к:', `http://localhost:8000/api/users/profile/${userId}/`);
    
    const response = await fetch(`http://localhost:8000/api/users/profile/${userId}/`, {
      headers: {
        'Authorization': `Bearer ${token}`,
        'Content-Type': 'application/json',
      }
    });

    console.log('Ответ API:', response.status, response.statusText);

    if (response.ok) {
      const data = await response.json();
      console.log('Данные пользователя:', data);
      
      const userName = data.user?.first_name || data.user?.username || `Пользователь ${userId}`;
      const userAvatar = data.user?.avatar || null;
      const userCity = data.user?.city || 'Не указан';
      
      console.log('Имя пользователя:', userName);
      console.log('Аватар пользователя:', userAvatar);
      console.log('Город пользователя:', userCity);
      
      const userInfo = {
        id: userId,
        name: userName,
        avatar: userAvatar,
        city: userCity
      };
      
      // Сохраняем полученную информацию в localStorage
      saveUserInfoToStorage(userInfo);
      
      return userInfo;
    } else {
      console.log('API вернул ошибку, возвращаем базовую информацию');
      const basicUserInfo = {
        id: userId,
        name: `Пользователь ${userId}`,
        avatar: null,
        city: 'Не указан'
      };
      saveUserInfoToStorage(basicUserInfo);
      return basicUserInfo;
    }
  } catch (error) {
    console.error('Ошибка при получении информации о пользователе:', error);
    console.log('Возвращаем базовую информацию из-за ошибки');
    const basicUserInfo = {
      id: userId,
      name: `Пользователь ${userId}`,
      avatar: null,
      city: 'Не указан'
    };
    saveUserInfoToStorage(basicUserInfo);
    return basicUserInfo;
  }
};

// Получить инициалы пользователя
export const getUserInitials = (userName) => {
  if (!userName) return 'А';
  const names = userName.split(' ');
  return `${names[0]?.charAt(0) || 'А'}${names[1]?.charAt(0) || ''}`.toUpperCase();
};

// Форматировать дату
export const formatDate = (dateString) => {
  if (!dateString) return 'Недавно';
  const date = new Date(dateString);
  const now = new Date();
  const diffInHours = Math.floor((now - date) / (1000 * 60 * 60));
  
  if (diffInHours < 1) return 'Только что';
  if (diffInHours < 24) return `${diffInHours} ч назад`;
  
  const diffInDays = Math.floor(diffInHours / 24);
  if (diffInDays < 7) return `${diffInDays} дн назад`;
  
  return date.toLocaleDateString('ru-RU');
};
