import React, { useState, useEffect, useRef } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { 
  faArrowLeft, 
  faPaperPlane, 
  faEdit, 
  faTrash, 
  faCheck, 
  faTimes,
  faUser,
  faMessage
} from '@fortawesome/free-solid-svg-icons';
import { getChat, saveMessage, updateMessage, deleteMessage } from '../../utils/chatStorage';
import { getUserInfo, getMockUserInfo, getUserInfoFromStorage } from '../../utils/userUtils';
import './ChatPage.css';

const ChatPage = () => {
  const { userId } = useParams();
  const navigate = useNavigate();
  const messagesEndRef = useRef(null);
  const [messages, setMessages] = useState([]);
  const [newMessage, setNewMessage] = useState('');
  const [editingMessageId, setEditingMessageId] = useState(null);
  const [editText, setEditText] = useState('');
  const [user, setUser] = useState(null);
  const [isLoading, setIsLoading] = useState(true);

  // –ó–∞–≥—Ä—É–∂–∞–µ–º –ø–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª—è –∏ —Å–æ–æ–±—â–µ–Ω–∏—è
  useEffect(() => {
    const loadChat = async () => {
      try {
        console.log('–ó–∞–≥—Ä—É–∂–∞–µ–º —á–∞—Ç –¥–ª—è –ø–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª—è:', userId);
        
        // –ó–∞–≥—Ä—É–∂–∞–µ–º —Å—É—â–µ—Å—Ç–≤—É—é—â–∏–µ —Å–æ–æ–±—â–µ–Ω–∏—è
        const existingMessages = getChat(userId);
        console.log('–°—É—â–µ—Å—Ç–≤—É—é—â–∏–µ —Å–æ–æ–±—â–µ–Ω–∏—è:', existingMessages);
        setMessages(existingMessages);

        // –ü–æ–ª—É—á–∞–µ–º –∏–Ω—Ñ–æ—Ä–º–∞—Ü–∏—é –æ –ø–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª–µ
        console.log('–ó–∞–ø—Ä–∞—à–∏–≤–∞–µ–º –∏–Ω—Ñ–æ—Ä–º–∞—Ü–∏—é –æ –ø–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª–µ...');
        
        // –°–Ω–∞—á–∞–ª–∞ –ø—Ä–æ–≤–µ—Ä—è–µ–º localStorage
        const storedUserInfo = getUserInfoFromStorage(userId);
        if (storedUserInfo) {
          console.log('–ù–∞–π–¥–µ–Ω–∞ –∏–Ω—Ñ–æ—Ä–º–∞—Ü–∏—è –æ –ø–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª–µ –≤ localStorage:', storedUserInfo);
          setUser(storedUserInfo);
          setIsLoading(false);
          return;
        }
        
        // –ï—Å–ª–∏ –≤ localStorage –Ω–µ—Ç, –¥–µ–ª–∞–µ–º API-–∑–∞–ø—Ä–æ—Å
        const userInfo = await getUserInfo(userId);
        console.log('–ü–æ–ª—É—á–µ–Ω–∞ –∏–Ω—Ñ–æ—Ä–º–∞—Ü–∏—è –æ –ø–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª–µ:', userInfo);
        
        // –ï—Å–ª–∏ –Ω–µ —É–¥–∞–ª–æ—Å—å –ø–æ–ª—É—á–∏—Ç—å –∏–Ω—Ñ–æ—Ä–º–∞—Ü–∏—é, –∏—Å–ø–æ–ª—å–∑—É–µ–º –º–æ–∫–æ–≤—ã–µ –¥–∞–Ω–Ω—ã–µ
        if (!userInfo || !userInfo.name) {
          console.log('–ò–Ω—Ñ–æ—Ä–º–∞—Ü–∏—è –æ –ø–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª–µ –Ω–µ –ø–æ–ª—É—á–µ–Ω–∞, –∏—Å–ø–æ–ª—å–∑—É–µ–º –º–æ–∫–æ–≤—ã–µ –¥–∞–Ω–Ω—ã–µ');
          const mockUserInfo = getMockUserInfo(userId);
          setUser(mockUserInfo);
        } else {
          setUser(userInfo);
        }
        
        setIsLoading(false);
      } catch (error) {
        console.error('–û—à–∏–±–∫–∞ –ø—Ä–∏ –∑–∞–≥—Ä—É–∑–∫–µ —á–∞—Ç–∞:', error);
        // Fallback –Ω–∞ –º–æ–∫–æ–≤—ã–µ –¥–∞–Ω–Ω—ã–µ
        const fallbackUser = getMockUserInfo(userId);
        console.log('–ò—Å–ø–æ–ª—å–∑—É–µ–º fallback –∏–Ω—Ñ–æ—Ä–º–∞—Ü–∏—é:', fallbackUser);
        setUser(fallbackUser);
        setIsLoading(false);
      }
    };

    loadChat();
  }, [userId]);

  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  };

  useEffect(() => {
    scrollToBottom();
  }, [messages]);

  const handleSendMessage = () => {
    if (newMessage.trim()) {
      const messageData = {
        text: newMessage.trim(),
        sender: 'user'
      };

      // –°–æ—Ö—Ä–∞–Ω—è–µ–º —Å–æ–æ–±—â–µ–Ω–∏–µ –≤ localStorage
      const savedMessage = saveMessage(userId, messageData);
      
      if (savedMessage) {
        setMessages(prev => [...prev, savedMessage]);
        setNewMessage('');
      }
    }
  };

  const handleKeyPress = (e) => {
    if (e.key === 'Enter' && !e.shiftKey) {
      e.preventDefault();
      handleSendMessage();
    }
  };

  const handleEditMessage = (messageId) => {
    const message = messages.find(msg => msg.id === messageId);
    if (message) {
      setEditingMessageId(messageId);
      setEditText(message.text);
    }
  };

  const handleSaveEdit = () => {
    if (editText.trim() && editingMessageId) {
      // –û–±–Ω–æ–≤–ª—è–µ–º —Å–æ–æ–±—â–µ–Ω–∏–µ –≤ localStorage
      const success = updateMessage(userId, editingMessageId, editText.trim());
      
      if (success) {
        setMessages(prev => prev.map(msg => 
          msg.id === editingMessageId 
            ? { ...msg, text: editText.trim(), isEdited: true, editedAt: new Date().toISOString() }
            : msg
        ));
        setEditingMessageId(null);
        setEditText('');
      }
    }
  };

  const handleCancelEdit = () => {
    setEditingMessageId(null);
    setEditText('');
  };

  const handleDeleteMessage = (messageId) => {
    if (window.confirm('–í—ã —É–≤–µ—Ä–µ–Ω—ã, —á—Ç–æ —Ö–æ—Ç–∏—Ç–µ —É–¥–∞–ª–∏—Ç—å —ç—Ç–æ —Å–æ–æ–±—â–µ–Ω–∏–µ?')) {
      // –£–¥–∞–ª—è–µ–º —Å–æ–æ–±—â–µ–Ω–∏–µ –∏–∑ localStorage
      const success = deleteMessage(userId, messageId);
      
      if (success) {
        setMessages(prev => prev.filter(msg => msg.id !== messageId));
      }
    }
  };

  const handleUseMockData = () => {
    console.log('–ü—Ä–∏–Ω—É–¥–∏—Ç–µ–ª—å–Ω–æ –∏—Å–ø–æ–ª—å–∑—É–µ–º –º–æ–∫–æ–≤—ã–µ –¥–∞–Ω–Ω—ã–µ');
    const mockUserInfo = getMockUserInfo(userId);
    console.log('–°–æ–∑–¥–∞–Ω–Ω—ã–µ –º–æ–∫–æ–≤—ã–µ –¥–∞–Ω–Ω—ã–µ:', mockUserInfo);
    setUser(mockUserInfo);
  };

  const handleClearStorage = () => {
    console.log('–û—á–∏—â–∞–µ–º localStorage –¥–ª—è –ø–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª—è:', userId);
    try {
      const storedUsers = localStorage.getItem('userProfiles');
      if (storedUsers) {
        const users = JSON.parse(storedUsers);
        delete users[userId];
        localStorage.setItem('userProfiles', JSON.stringify(users));
        console.log('localStorage –æ—á–∏—â–µ–Ω –¥–ª—è –ø–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª—è:', userId);
      }
    } catch (error) {
      console.error('–û—à–∏–±–∫–∞ –ø—Ä–∏ –æ—á–∏—Å—Ç–∫–µ localStorage:', error);
    }
  };

  const formatTime = (timestamp) => {
    const date = new Date(timestamp);
    return date.toLocaleTimeString('ru-RU', { 
      hour: '2-digit', 
      minute: '2-digit' 
    });
  };

  const formatDate = (timestamp) => {
    const date = new Date(timestamp);
    const today = new Date();
    const yesterday = new Date(today);
    yesterday.setDate(yesterday.getDate() - 1);

    if (date.toDateString() === today.toDateString()) {
      return '–°–µ–≥–æ–¥–Ω—è';
    } else if (date.toDateString() === yesterday.toDateString()) {
      return '–í—á–µ—Ä–∞';
    } else {
      return date.toLocaleDateString('ru-RU');
    }
  };

  if (isLoading) {
    console.log('–°–æ—Å—Ç–æ—è–Ω–∏–µ –∑–∞–≥—Ä—É–∑–∫–∏: isLoading = true');
    return (
      <div className="chat-loading">
        <div className="loading-spinner"></div>
        <p>–ó–∞–≥—Ä—É–∑–∫–∞ —á–∞—Ç–∞...</p>
      </div>
    );
  }

  console.log('–†–µ–Ω–¥–µ—Ä–∏–º —á–∞—Ç. –°–æ—Å—Ç–æ—è–Ω–∏–µ –ø–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª—è:', user);
  console.log('–°–æ—Å—Ç–æ—è–Ω–∏–µ –∑–∞–≥—Ä—É–∑–∫–∏: isLoading = false');

  // –ü—Ä–æ–≤–µ—Ä—è–µ–º, —á—Ç–æ —É –Ω–∞—Å –µ—Å—Ç—å –∏–Ω—Ñ–æ—Ä–º–∞—Ü–∏—è –æ –ø–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª–µ
  if (!user) {
    console.log('–ò–Ω—Ñ–æ—Ä–º–∞—Ü–∏—è –æ –ø–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª–µ –æ—Ç—Å—É—Ç—Å—Ç–≤—É–µ—Ç, –ø–æ–∫–∞–∑—ã–≤–∞–µ–º –∑–∞–≥—Ä—É–∑–∫—É');
    return (
      <div className="chat-loading">
        <div className="loading-spinner"></div>
        <p>–ó–∞–≥—Ä—É–∑–∫–∞ –∏–Ω—Ñ–æ—Ä–º–∞—Ü–∏–∏ –æ –ø–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª–µ...</p>
      </div>
    );
  }

  return (
    <div className="chat-page">
      {/* –ó–∞–≥–æ–ª–æ–≤–æ–∫ —á–∞—Ç–∞ */}
      <div className="chat-header">
        <button className="back-button" onClick={() => navigate(-1)}>
          <FontAwesomeIcon icon={faArrowLeft} />
        </button>
        
        <div className="chat-user-info">
          <div className="user-avatar">
            {console.log('–†–µ–Ω–¥–µ—Ä–∏–º –∞–≤–∞—Ç–∞—Ä –ø–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª—è:', user?.avatar)}
            {user?.avatar ? (
              <img src={user.avatar} alt={user.name} />
            ) : (
              <div className="avatar-placeholder">
                <FontAwesomeIcon icon={faUser} />
              </div>
            )}
          </div>
          
          <div className="user-details">
            <h3 className="user-name">
              {user?.name || '–ó–∞–≥—Ä—É–∑–∫–∞...'}
              {console.log('–†–µ–Ω–¥–µ—Ä–∏–º –∏–º—è –ø–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª—è:', user?.name)}
            </h3>
            {user?.city && (
              <span className="user-city">
                üìç {user.city}
              </span>
            )}
            {process.env.NODE_ENV === 'development' && (
              <>
                <small style={{ color: '#666', fontSize: '12px' }}>
                  ID: {user?.id} | –û—Ç–ª–∞–¥–∫–∞: {JSON.stringify(user)}
                </small>
                <small style={{ color: '#999', fontSize: '10px', display: 'block', marginTop: '2px' }}>
                  –ò—Å—Ç–æ—á–Ω–∏–∫: {user?.name && user.name !== `–ü–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª—å ${userId}` ? 'API/Storage' : '–ú–æ–∫–æ–≤—ã–µ –¥–∞–Ω–Ω—ã–µ'}
                </small>
                <button 
                  onClick={handleUseMockData}
                  style={{ 
                    marginTop: '5px', 
                    padding: '2px 8px', 
                    fontSize: '10px', 
                    background: '#667eea', 
                    color: 'white', 
                    border: 'none', 
                    borderRadius: '4px',
                    cursor: 'pointer'
                  }}
                >
                  –ò—Å–ø–æ–ª—å–∑–æ–≤–∞—Ç—å –º–æ–∫–æ–≤—ã–µ –¥–∞–Ω–Ω—ã–µ
                </button>
                <button 
                  onClick={handleClearStorage}
                  style={{ 
                    marginTop: '5px', 
                    padding: '2px 8px', 
                    fontSize: '10px', 
                    background: '#e74c3c', 
                    color: 'white', 
                    border: 'none', 
                    borderRadius: '4px',
                    cursor: 'pointer'
                  }}
                >
                  –û—á–∏—Å—Ç–∏—Ç—å localStorage
                </button>
              </>
            )}
          </div>
        </div>
      </div>

      {/* –ö–æ–Ω—Ç–µ–π–Ω–µ—Ä —Å–æ–æ–±—â–µ–Ω–∏–π */}
      <div className="messages-container">
        {messages.length === 0 ? (
          <div className="empty-chat">
            <div className="empty-chat-icon">
              <FontAwesomeIcon icon={faMessage} />
            </div>
            <h3>–ß–∞—Ç –ø—É—Å—Ç</h3>
            <p>–ù–∞—á–Ω–∏—Ç–µ —Ä–∞–∑–≥–æ–≤–æ—Ä, –æ—Ç–ø—Ä–∞–≤–∏–≤ –ø–µ—Ä–≤–æ–µ —Å–æ–æ–±—â–µ–Ω–∏–µ</p>
          </div>
        ) : (
          <div className="messages-list">
            {messages.map((message) => (
              <div key={message.id} className={`message ${message.sender === 'user' ? 'user-message' : 'other-message'}`}>
                {editingMessageId === message.id ? (
                  <div className="edit-message-form">
                    <textarea
                      className="edit-textarea"
                      value={editText}
                      onChange={(e) => setEditText(e.target.value)}
                      placeholder="–†–µ–¥–∞–∫—Ç–∏—Ä–æ–≤–∞—Ç—å —Å–æ–æ–±—â–µ–Ω–∏–µ..."
                      autoFocus
                    />
                    <div className="edit-actions">
                      <button 
                        className="edit-btn save-btn" 
                        onClick={handleSaveEdit}
                      >
                        <FontAwesomeIcon icon={faCheck} />
                      </button>
                      <button 
                        className="edit-btn cancel-btn" 
                        onClick={handleCancelEdit}
                      >
                        <FontAwesomeIcon icon={faTimes} />
                      </button>
                    </div>
                  </div>
                ) : (
                  <>
                    <div className="message-content">
                      <p className="message-text">{message.text}</p>
                      {message.isEdited && (
                        <span className="edited-indicator">(–∏–∑–º–µ–Ω–µ–Ω–æ)</span>
                      )}
                      <div className="message-meta">
                        <span className="message-time">{formatTime(message.timestamp)}</span>
                      </div>
                    </div>
                    
                    {/* –ö–Ω–æ–ø–∫–∏ –¥–µ–π—Å—Ç–≤–∏–π –¥–ª—è —Å–æ–æ–±—â–µ–Ω–∏–π –ø–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª—è */}
                    {message.sender === 'user' && (
                      <div className="message-actions">
                        <button 
                          className="action-btn edit-btn" 
                          onClick={() => handleEditMessage(message.id)}
                          title="–†–µ–¥–∞–∫—Ç–∏—Ä–æ–≤–∞—Ç—å"
                        >
                          <FontAwesomeIcon icon={faEdit} />
                        </button>
                        <button 
                          className="action-btn delete-btn" 
                          onClick={() => handleDeleteMessage(message.id)}
                          title="–£–¥–∞–ª–∏—Ç—å"
                        >
                          <FontAwesomeIcon icon={faTrash} />
                        </button>
                      </div>
                    )}
                  </>
                )}
              </div>
            ))}
            <div ref={messagesEndRef} />
          </div>
        )}
      </div>

      {/* –§–æ—Ä–º–∞ –≤–≤–æ–¥–∞ —Å–æ–æ–±—â–µ–Ω–∏—è */}
      <div className="message-input-container">
        <div className="message-input-wrapper">
          <textarea
            className="message-input"
            value={newMessage}
            onChange={(e) => setNewMessage(e.target.value)}
            onKeyPress={handleKeyPress}
            placeholder="–í–≤–µ–¥–∏—Ç–µ —Å–æ–æ–±—â–µ–Ω–∏–µ..."
            rows="1"
          />
          <button 
            className="send-button" 
            onClick={handleSendMessage}
            disabled={!newMessage.trim()}
          >
            <FontAwesomeIcon icon={faPaperPlane} />
          </button>
        </div>
      </div>
    </div>
  );
};

export default ChatPage;
