import React from 'react';
import './CommunityStats.css';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faUsers, faComments, faHeart, faStar, faCalendarAlt, faMapMarkerAlt } from '@fortawesome/free-solid-svg-icons';

const CommunityStats = () => {
  const stats = [
    {
      icon: faUsers,
      value: '50,000+',
      label: '–ê–∫—Ç–∏–≤–Ω—ã—Ö –º–∞–º',
      color: 'blue'
    },
    {
      icon: faComments,
      value: '1,000+',
      label: '–ü–æ—Å—Ç–æ–≤ –≤ –¥–µ–Ω—å',
      color: 'green'
    },
    {
      icon: faHeart,
      value: '100,000+',
      label: '–õ–∞–π–∫–æ–≤ –≤ –º–µ—Å—è—Ü',
      color: 'pink'
    },
    {
      icon: faStar,
      value: '4.9',
      label: '–†–µ–π—Ç–∏–Ω–≥ —Å–æ–æ–±—â–µ—Å—Ç–≤–∞',
      color: 'yellow'
    },
    {
      icon: faCalendarAlt,
      value: '15 –ª–µ—Ç',
      label: '–û–ø—ã—Ç–∞ —Ä–∞–±–æ—Ç—ã',
      color: 'purple'
    },
    {
      icon: faMapMarkerAlt,
      value: '150+',
      label: '–ì–æ—Ä–æ–¥–æ–≤ –†–æ—Å—Å–∏–∏',
      color: 'orange'
    }
  ];

  return (
    <div className="community-stats-section">
      <div className="stats-header">
        <h3>üìä –ù–∞—à–µ —Å–æ–æ–±—â–µ—Å—Ç–≤–æ –≤ —Ü–∏—Ñ—Ä–∞—Ö</h3>
        <p>–ü—Ä–∏—Å–æ–µ–¥–∏–Ω—è–π—Ç–µ—Å—å –∫ —Å–∞–º–æ–π –±–æ–ª—å—à–æ–π —Å–µ—Ç–∏ –º–∞–º –≤ –†–æ—Å—Å–∏–∏</p>
      </div>
      
      <div className="stats-grid">
        {stats.map((stat, index) => (
          <div key={index} className={`stat-card ${stat.color}`}>
            <div className="stat-icon">
              <FontAwesomeIcon icon={stat.icon} />
            </div>
            <div className="stat-content">
              <div className="stat-value">{stat.value}</div>
              <div className="stat-label">{stat.label}</div>
            </div>
          </div>
        ))}
      </div>
      
      <div className="stats-footer">
        <div className="join-cta">
          <h4>–ì–æ—Ç–æ–≤—ã –ø—Ä–∏—Å–æ–µ–¥–∏–Ω–∏—Ç—å—Å—è?</h4>
          <p>–°–æ–∑–¥–∞–π—Ç–µ –∞–∫–∫–∞—É–Ω—Ç –∏ –Ω–∞—á–Ω–∏—Ç–µ –æ–±—â–∞—Ç—å—Å—è —Å –¥—Ä—É–≥–∏–º–∏ –º–∞–º–∞–º–∏ —É–∂–µ —Å–µ–≥–æ–¥–Ω—è!</p>
          <button className="btn-join">–ü—Ä–∏—Å–æ–µ–¥–∏–Ω–∏—Ç—å—Å—è –∫ —Å–æ–æ–±—â–µ—Å—Ç–≤—É</button>
        </div>
      </div>
    </div>
  );
};

export default CommunityStats;
