import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { 
  faHeart, 
  faComment, 
  faEye, 
  faUser, 
  faCalendar,
  faTag,
  faArrowLeft
} from '@fortawesome/free-solid-svg-icons';
import './PostDetail.css';

const PostDetail = () => {
  const { slug } = useParams();
  const navigate = useNavigate();
  const [post, setPost] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [isLiked, setIsLiked] = useState(false);

  useEffect(() => {
    fetchPost();
  }, [slug]);

  const fetchPost = async () => {
    try {
      setLoading(true);
      const response = await fetch(`http://localhost:8000/api/posts/${slug}/`);
      
      if (response.ok) {
        const data = await response.json();
        setPost(data);
        setIsLiked(data.is_liked || false);
      } else {
        setError('Пост не найден');
      }
    } catch (error) {
      console.error('Ошибка загрузки поста:', error);
      setError('Ошибка загрузки поста');
    } finally {
      setLoading(false);
    }
  };

  const handleLike = async () => {
    try {
      const token = localStorage.getItem('accessToken') || sessionStorage.getItem('accessToken');
      const response = await fetch(`http://localhost:8000/api/posts/${slug}/like/`, {
        method: 'POST',
        headers: {
          'Authorization': `Bearer ${token}`,
          'Content-Type': 'application/json'
        }
      });

      if (response.ok) {
        const data = await response.json();
        setIsLiked(data.is_liked);
        setPost(prev => ({
          ...prev,
          likes_count: data.likes_count
        }));
      }
    } catch (error) {
      console.error('Ошибка при лайке:', error);
    }
  };

  if (loading) {
    return (
      <div className="post-detail-loading">
        <div className="loading-spinner"></div>
        <p>Загрузка поста...</p>
      </div>
    );
  }

  if (error || !post) {
    return (
      <div className="post-detail-error">
        <h2>Ошибка</h2>
        <p>{error || 'Пост не найден'}</p>
        <button onClick={() => navigate('/')} className="btn-back">
          <FontAwesomeIcon icon={faArrowLeft} />
          Вернуться на главную
        </button>
      </div>
    );
  }

  return (
    <div className="post-detail">
      {/* Кнопка назад */}
      <div className="post-detail-header">
        <button onClick={() => navigate('/')} className="btn-back">
          <FontAwesomeIcon icon={faArrowLeft} />
          Назад
        </button>
      </div>

      {/* Основной контент */}
      <div className="post-detail-content">
        <article className="post-article">
          {/* Заголовок и мета-информация */}
          <header className="post-header">
            <h1 className="post-title">{post.title}</h1>
            
            <div className="post-meta">
              <div className="post-author">
                <FontAwesomeIcon icon={faUser} />
                <span>{post.author.first_name} {post.author.last_name}</span>
              </div>
              
              <div className="post-date">
                <FontAwesomeIcon icon={faCalendar} />
                <span>{new Date(post.published_at || post.created_at).toLocaleDateString('ru-RU')}</span>
              </div>
              
              <div className="post-category">
                <FontAwesomeIcon icon={faTag} />
                <span>{post.category.name}</span>
              </div>
            </div>

            {/* Статистика */}
            <div className="post-stats">
              <div className="stat-item">
                <FontAwesomeIcon icon={faEye} />
                <span>{post.views_count} просмотров</span>
              </div>
              
              <button 
                className={`stat-item like-button ${isLiked ? 'liked' : ''}`}
                onClick={handleLike}
              >
                <FontAwesomeIcon icon={faHeart} />
                <span>{post.likes_count} лайков</span>
              </button>
              
              <div className="stat-item">
                <FontAwesomeIcon icon={faComment} />
                <span>{post.comments_count} комментариев</span>
              </div>
            </div>
          </header>

          {/* Краткое описание */}
          {post.short_description && (
            <div className="post-short-description">
              <p>{post.short_description}</p>
            </div>
          )}

          {/* Основной контент */}
          <div className="post-body">
            <div className="post-content" dangerouslySetInnerHTML={{ __html: post.content }} />
          </div>

          {/* SEO информация (только для автора или админа) */}
          {post.author.id === (JSON.parse(localStorage.getItem('userData') || '{}').id) && (
            <div className="post-seo-info">
              <h3>SEO информация</h3>
              <div className="seo-fields">
                <div className="seo-field">
                  <label>Meta Title:</label>
                  <p>{post.meta_title}</p>
                </div>
                
                <div className="seo-field">
                  <label>Meta Description:</label>
                  <p>{post.meta_description}</p>
                </div>
                
                <div className="seo-field">
                  <label>Meta Keywords:</label>
                  <p>{post.meta_keywords}</p>
                </div>
              </div>
            </div>
          )}

          {/* Комментарии */}
          {post.comments_enabled === 'enabled' && (
            <div className="post-comments">
              <h3>Комментарии ({post.comments.length})</h3>
              {post.comments.length > 0 ? (
                <div className="comments-list">
                  {post.comments.map(comment => (
                    <div key={comment.id} className="comment">
                      <div className="comment-header">
                        <span className="comment-author">{comment.author.first_name} {comment.author.last_name}</span>
                        <span className="comment-date">{comment.created_at}</span>
                      </div>
                      <div className="comment-content">{comment.content}</div>
                    </div>
                  ))}
                </div>
              ) : (
                <p className="no-comments">Пока нет комментариев. Будьте первым!</p>
              )}
            </div>
          )}
        </article>

        {/* Боковая панель */}
        <aside className="post-sidebar">
          <div className="sidebar-section">
            <h3>Информация о посте</h3>
            <div className="post-info">
              <p><strong>Статус:</strong> {post.status === 'published' ? 'Опубликован' : 'Черновик'}</p>
              <p><strong>Комментарии:</strong> {post.comments_enabled === 'enabled' ? 'Разрешены' : 'Запрещены'}</p>
              <p><strong>Дата создания:</strong> {new Date(post.created_at).toLocaleDateString('ru-RU')}</p>
              {post.published_at && (
                <p><strong>Дата публикации:</strong> {new Date(post.published_at).toLocaleDateString('ru-RU')}</p>
              )}
            </div>
          </div>
        </aside>
      </div>
    </div>
  );
};

export default PostDetail;
