import React, { useState, useEffect, useRef } from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faPaperPlane, faTimes } from '@fortawesome/free-solid-svg-icons';
import './ChatModal.css';

const ChatModal = ({ isOpen, onClose, userId, userName, onChatCreated }) => {
  const [messages, setMessages] = useState([]);
  const [newMessage, setNewMessage] = useState('');
  const [user, setUser] = useState(null);
  const [loading, setLoading] = useState(false);
  const chatIdRef = useRef(null);

  useEffect(() => {
    if (isOpen && userId) {
      loadUserData();
      loadChatMessages();
    }
  }, [isOpen, userId]);

  const loadUserData = async () => {
    try {
      const token = localStorage.getItem('accessToken') || sessionStorage.getItem('accessToken');
      const response = await fetch(`http://localhost:8000/users/${userId}/`, {
        headers: {
          'Authorization': `Bearer ${token}`,
          'Content-Type': 'application/json',
        },
      });

      if (response.ok) {
        const data = await response.json();
        if (data.success && data.user) {
          setUser(data.user);
        }
      }
    } catch (error) {
      console.error('Ошибка загрузки данных пользователя:', error);
    }
  };

  const loadChatMessages = async () => {
    try {
      const token = localStorage.getItem('accessToken') || sessionStorage.getItem('accessToken');

      // Сначала проверяем, есть ли уже чат с этим пользователем
      const chatResponse = await fetch(`http://localhost:8000/api/users/chats/`, {
        headers: {
          'Authorization': `Bearer ${token}`,
          'Content-Type': 'application/json',
        },
      });

      if (chatResponse.ok) {
        const chatData = await chatResponse.json();
        const existingChat = chatData.chats.find(chat => 
          chat.participants.some(p => p.id === parseInt(userId))
        );

        if (existingChat) {
          chatIdRef.current = existingChat.id;
          // Загружаем сообщения существующего чата
          const messagesResponse = await fetch(`http://localhost:8000/api/users/chats/${existingChat.id}/`, {
            headers: {
              'Authorization': `Bearer ${token}`,
              'Content-Type': 'application/json',
            },
          });

          if (messagesResponse.ok) {
            const messagesData = await messagesResponse.json();
            console.log('Загружены сообщения:', messagesData);
            if (messagesData.success && messagesData.messages) {
              setMessages(messagesData.messages);
            }
          }
        } else {
          // Чат не существует, показываем пустой список
          setMessages([]);
        }
      }
    } catch (error) {
      console.error('Ошибка загрузки сообщений:', error);
      setMessages([]);
    }
  };

  const handleSendMessage = async (e) => {
    e.preventDefault();
    if (!newMessage.trim()) return;

    setLoading(true);
    try {
      const token = localStorage.getItem('accessToken') || sessionStorage.getItem('accessToken');

      // Сначала создаем чат, если его еще нет
      let chatId = chatIdRef.current;
      if (!chatId) {
        const chatResponse = await fetch(`http://localhost:8000/api/users/chats/create/`, {
          method: 'POST',
          headers: {
            'Authorization': `Bearer ${token}`,
            'Content-Type': 'application/json',
          },
          body: JSON.stringify({
            participant_id: userId
          }),
        });

        if (chatResponse.ok) {
          const chatData = await chatResponse.json();
          chatId = chatData.chat.id;
          chatIdRef.current = chatId;
          console.log('Чат создан:', chatId);

          // Уведомляем родительский компонент о создании чата
          if (onChatCreated) {
            onChatCreated();
          }
        } else {
          const errorData = await chatResponse.json();
          console.error('Ошибка создания чата:', chatResponse.status, errorData);
          throw new Error(`Не удалось создать чат: ${errorData.message || errorData.errors || 'Неизвестная ошибка'}`);
        }
      }

      // Отправляем сообщение
      console.log(`Отправляем сообщение в чат ${chatId}:`, newMessage);
      const response = await fetch(`http://localhost:8000/api/users/chats/${chatId}/messages/`, {
        method: 'POST',
        headers: {
          'Authorization': `Bearer ${token}`,
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          content: newMessage,
          message_type: 'text'
        }),
      });

      if (response.ok) {
        const data = await response.json();
        if (data.success && data.message) {
          setMessages(prev => [...prev, data.message]);
          setNewMessage('');
        }
      } else {
        const errorData = await response.json();
        console.error('Ошибка отправки сообщения:', response.status, errorData);
        alert(`Не удалось отправить сообщение: ${errorData.message || 'Неизвестная ошибка'}`);
      }
    } catch (error) {
      console.error('Ошибка отправки сообщения:', error);
    } finally {
      setLoading(false);
    }
  };

  if (!isOpen) return null;

  const displayName = user ? 
    (user.first_name && user.last_name ? `${user.first_name} ${user.last_name}` : 
     user.first_name || user.username || 'Пользователь') : 
    userName || 'Пользователь';

  return (
    <div className="chat-modal-overlay" onClick={onClose}>
      <div className="chat-modal" onClick={(e) => e.stopPropagation()}>
        {/* Заголовок модального окна */}
        <div className="chat-modal-header">
          <div className="chat-user-info">
            <h3 className="user-name">{displayName}</h3>
            <p className="user-status">Была онлайн: 3 м</p>
          </div>
          <button className="close-button" onClick={onClose}>
            <FontAwesomeIcon icon={faTimes} />
          </button>
        </div>

        {/* Контейнер сообщений */}
        <div className="chat-modal-messages">
          {messages.length === 0 ? (
            <div className="empty-messages">
              <p className="empty-title">Сообщений нет.</p>
              <p className="empty-subtitle">Собеседник не видит эту переписку.</p>
            </div>
          ) : (
            <div className="messages-list">
              {messages.map((message) => {
                const currentUserId = parseInt(localStorage.getItem('user_id') || sessionStorage.getItem('user_id'));
                const isMyMessage = message.sender === currentUserId;

                return (
                  <div key={message.id} className={`message ${isMyMessage ? 'my-message' : 'incoming-message'}`}>
                    <div className="message-content" style={isMyMessage ? {backgroundColor: '#007bff', color: 'white'} : {backgroundColor: '#ffffff', color: 'black'}}>
                      <p>{message.content}</p>
                      <span className="message-time">
                        {new Date(message.created_at).toLocaleTimeString('ru-RU', {
                          hour: '2-digit',
                          minute: '2-digit'
                        })}
                      </span>
                      {/* Отладочная информация */}
                      <div style={{fontSize: '10px', opacity: 0.7, marginTop: '4px'}}>
                        {isMyMessage ? 'МОЕ' : 'НЕ МОЕ'} (ID: {message.sender})
                      </div>
                    </div>
                  </div>
                );
              })}
            </div>
          )}
        </div>

        {/* Поле ввода сообщения */}
        <div className="chat-modal-input">
          <div className="message-input-wrapper">
            <input
              type="text"
              className="message-input"
              placeholder="Ваше сообщение"
              value={newMessage}
              onChange={(e) => setNewMessage(e.target.value)}
              onKeyPress={(e) => e.key === 'Enter' && handleSendMessage(e)}
            />
            <button 
              className="send-button" 
              onClick={handleSendMessage}
              disabled={!newMessage.trim() || loading}
            >
              <FontAwesomeIcon icon={faPaperPlane} />
              <span>Отправить</span>
            </button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default ChatModal;
