import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import {
  faPen,
  faUser,
  faBaby,
  faEdit,
  faCog,
  faPlus,
  faTimes,
  faMapMarkerAlt,
  faEye
} from '@fortawesome/free-solid-svg-icons';
import profileData from '../../data/profileData.json';
import './ProfilePage.css';

const ProfilePage = () => {
  const { userId } = useParams();
  const navigate = useNavigate();
  const [profile, setProfile] = useState(profileData.profile);
  const [activeTab, setActiveTab] = useState('posts');
  const [selectedCategory, setSelectedCategory] = useState('Ð’ÑÐµ ÐºÐ°Ñ‚ÐµÐ³Ð¾Ñ€Ð¸Ð¸');
  const [showAddChildModal, setShowAddChildModal] = useState(false);
  const [posts, setPosts] = useState([]);
  const [drafts, setDrafts] = useState([]);
  const [loading, setLoading] = useState(false);
  const [showEditProfileModal, setShowEditProfileModal] = useState(false);
  const [childData, setChildData] = useState({
    name: '',
    gender: '',
    age: ''
  });
  const [editProfileData, setEditProfileData] = useState({
    city: '',
    birthDate: '',
    status: ''
  });
  const [children, setChildren] = useState([]);

  useEffect(() => {
    // ÐŸÐ¾Ð»ÑƒÑ‡Ð°ÐµÐ¼ Ð´Ð°Ð½Ð½Ñ‹Ðµ Ð¿Ð¾Ð»ÑŒÐ·Ð¾Ð²Ð°Ñ‚ÐµÐ»Ñ Ð¸Ð· localStorage (ÐµÑÐ»Ð¸ ÐµÑÑ‚ÑŒ)
    const userData = localStorage.getItem('userData');
    if (userData) {
      const user = JSON.parse(userData);
      console.log('Ð—Ð°Ð³Ñ€ÑƒÐ¶ÐµÐ½Ð½Ñ‹Ðµ Ð´Ð°Ð½Ð½Ñ‹Ðµ Ð¿Ð¾Ð»ÑŒÐ·Ð¾Ð²Ð°Ñ‚ÐµÐ»Ñ:', user);
      console.log('ÐÐ²Ð°Ñ‚Ð°Ñ€ Ð¿Ð¾Ð»ÑŒÐ·Ð¾Ð²Ð°Ñ‚ÐµÐ»Ñ:', user.avatar);
      
      setProfile(prev => ({
        ...prev,
        username: user.first_name || user.username || prev.username,
        fullName: `${user.first_name || ''} ${user.last_name || ''}`.trim(),
        avatar: user.first_name ? `${user.first_name[0]}${user.last_name ? user.last_name[0] : ''}` : prev.avatar,
        location: user.city || prev.location,
        personalInfo: {
          ...prev.personalInfo,
          firstName: user.first_name || prev.personalInfo.firstName,
          lastName: user.last_name || prev.personalInfo.lastName,
          email: user.email || prev.personalInfo.email,
          status: user.status || prev.personalInfo.status,
          city: user.city || prev.personalInfo.city,
          birthDate: user.birth_date || prev.personalInfo.birthDate,
          avatar: user.avatar || prev.personalInfo.avatar
        }
      }));

      // Ð˜Ð½Ð¸Ñ†Ð¸Ð°Ð»Ð¸Ð·Ð¸Ñ€ÑƒÐµÐ¼ Ð´Ð°Ð½Ð½Ñ‹Ðµ Ð´Ð»Ñ Ñ€ÐµÐ´Ð°ÐºÑ‚Ð¸Ñ€Ð¾Ð²Ð°Ð½Ð¸Ñ Ð¿Ñ€Ð¾Ñ„Ð¸Ð»Ñ
      setEditProfileData({
        city: user.city || '',
        birthDate: user.birth_date || '',
        status: user.status || ''
      });

      // Ð—Ð°Ð³Ñ€ÑƒÐ¶Ð°ÐµÐ¼ Ð´ÐµÑ‚ÐµÐ¹ Ð¸Ð· localStorage
      const savedChildren = JSON.parse(localStorage.getItem('children') || '[]');
      setChildren(savedChildren);
    }
    
    // Ð—Ð°Ð³Ñ€ÑƒÐ¶Ð°ÐµÐ¼ Ð¿Ð¾ÑÑ‚Ñ‹ Ð¿Ð¾Ð»ÑŒÐ·Ð¾Ð²Ð°Ñ‚ÐµÐ»Ñ
    fetchUserPosts();
  }, [userId]);

  const fetchUserPosts = async () => {
    setLoading(true);
    try {
      const token = localStorage.getItem('accessToken') || sessionStorage.getItem('accessToken');
      const response = await fetch('http://localhost:8000/api/posts/user-posts/', {
        headers: {
          'Authorization': `Bearer ${token}`
        }
      });
      
      if (response.ok) {
        const data = await response.json();
        const publishedPosts = data.posts.filter(post => post.status === 'published');
        const draftPosts = data.posts.filter(post => post.status === 'draft');
        
        setPosts(publishedPosts);
        setDrafts(draftPosts);
      } else {
        console.error('ÐžÑˆÐ¸Ð±ÐºÐ° Ð·Ð°Ð³Ñ€ÑƒÐ·ÐºÐ¸ Ð¿Ð¾ÑÑ‚Ð¾Ð²:', response.status);
      }
    } catch (error) {
      console.error('ÐžÑˆÐ¸Ð±ÐºÐ° Ð·Ð°Ð³Ñ€ÑƒÐ·ÐºÐ¸ Ð¿Ð¾ÑÑ‚Ð¾Ð²:', error);
    } finally {
      setLoading(false);
    }
  };

  const handleTabChange = (tabId) => {
    setActiveTab(tabId);
  };

  const handleWritePost = () => {
    navigate('/create-post');
  };

  const handlePublishDraft = async (draftId) => {
    try {
      const token = localStorage.getItem('accessToken') || sessionStorage.getItem('accessToken');
      const response = await fetch(`http://localhost:8000/api/posts/${draftId}/publish/`, {
        method: 'PATCH',
        headers: {
          'Authorization': `Bearer ${token}`,
          'Content-Type': 'application/json'
        },
        body: JSON.stringify({ status: 'published' })
      });
      
      if (response.ok) {
        // ÐžÐ±Ð½Ð¾Ð²Ð»ÑÐµÐ¼ ÑÐ¿Ð¸ÑÐºÐ¸ Ð¿Ð¾ÑÑ‚Ð¾Ð² Ð¸ Ñ‡ÐµÑ€Ð½Ð¾Ð²Ð¸ÐºÐ¾Ð²
        await fetchUserPosts();
        alert('Ð§ÐµÑ€Ð½Ð¾Ð²Ð¸Ðº ÑƒÑÐ¿ÐµÑˆÐ½Ð¾ Ð¾Ð¿ÑƒÐ±Ð»Ð¸ÐºÐ¾Ð²Ð°Ð½!');
      } else {
        alert('ÐžÑˆÐ¸Ð±ÐºÐ° Ð¿Ñ€Ð¸ Ð¿ÑƒÐ±Ð»Ð¸ÐºÐ°Ñ†Ð¸Ð¸ Ñ‡ÐµÑ€Ð½Ð¾Ð²Ð¸ÐºÐ°');
      }
    } catch (error) {
      console.error('ÐžÑˆÐ¸Ð±ÐºÐ° Ð¿ÑƒÐ±Ð»Ð¸ÐºÐ°Ñ†Ð¸Ð¸ Ñ‡ÐµÑ€Ð½Ð¾Ð²Ð¸ÐºÐ°:', error);
      alert('ÐŸÑ€Ð¾Ð¸Ð·Ð¾ÑˆÐ»Ð° Ð¾ÑˆÐ¸Ð±ÐºÐ° Ð¿Ñ€Ð¸ Ð¿ÑƒÐ±Ð»Ð¸ÐºÐ°Ñ†Ð¸Ð¸');
    }
  };

  const handleEditProfile = () => {
    setShowEditProfileModal(true);
  };

  const handleAddChild = () => {
    setShowAddChildModal(true);
  };

  const handleCloseModal = () => {
    setShowAddChildModal(false);
    setShowEditProfileModal(false);
    setChildData({ name: '', gender: '', age: '' });
  };

  const handleEditProfileDataChange = (field, value) => {
    setEditProfileData(prev => ({
      ...prev,
      [field]: value
    }));
  };

  const handleSaveProfile = () => {
    // ÐžÐ±Ð½Ð¾Ð²Ð»ÑÐµÐ¼ Ð´Ð°Ð½Ð½Ñ‹Ðµ Ð² localStorage
    const userData = JSON.parse(localStorage.getItem('userData') || '{}');
    const updatedUserData = {
      ...userData,
      city: editProfileData.city,
      birth_date: editProfileData.birthDate,
      status: editProfileData.status
    };
    localStorage.setItem('userData', JSON.stringify(updatedUserData));

    // ÐžÐ±Ð½Ð¾Ð²Ð»ÑÐµÐ¼ ÑÐ¾ÑÑ‚Ð¾ÑÐ½Ð¸Ðµ Ð¿Ñ€Ð¾Ñ„Ð¸Ð»Ñ
    setProfile(prev => ({
      ...prev,
      location: editProfileData.city,
      personalInfo: {
        ...prev.personalInfo,
        city: editProfileData.city,
        birthDate: editProfileData.birthDate,
        status: editProfileData.status
      }
    }));

    // Ð—Ð°ÐºÑ€Ñ‹Ð²Ð°ÐµÐ¼ Ð¼Ð¾Ð´Ð°Ð»ÑŒÐ½Ð¾Ðµ Ð¾ÐºÐ½Ð¾
    setShowEditProfileModal(false);
  };

  const handleChildDataChange = (field, value) => {
    setChildData(prev => ({
      ...prev,
      [field]: value
    }));
  };

  const handleSaveChild = () => {
    if (childData.name && childData.gender && childData.age) {
      // Ð—Ð´ÐµÑÑŒ Ð¼Ð¾Ð¶Ð½Ð¾ ÑÐ¾Ñ…Ñ€Ð°Ð½Ð¸Ñ‚ÑŒ Ð´Ð°Ð½Ð½Ñ‹Ðµ Ñ€ÐµÐ±ÐµÐ½ÐºÐ° Ð² localStorage Ð¸Ð»Ð¸ Ð¾Ñ‚Ð¿Ñ€Ð°Ð²Ð¸Ñ‚ÑŒ Ð½Ð° ÑÐµÑ€Ð²ÐµÑ€
      console.log('Ð”Ð°Ð½Ð½Ñ‹Ðµ Ñ€ÐµÐ±ÐµÐ½ÐºÐ°:', childData);
      
      // Ð¡Ð¾Ñ…Ñ€Ð°Ð½ÑÐµÐ¼ Ð² localStorage Ð´Ð»Ñ Ð´ÐµÐ¼Ð¾Ð½ÑÑ‚Ñ€Ð°Ñ†Ð¸Ð¸
      const existingChildren = JSON.parse(localStorage.getItem('children') || '[]');
      const newChild = {
        id: Date.now(),
        ...childData
      };
      const updatedChildren = [...existingChildren, newChild];
      localStorage.setItem('children', JSON.stringify(updatedChildren));
      
      // ÐžÐ±Ð½Ð¾Ð²Ð»ÑÐµÐ¼ ÑÐ¾ÑÑ‚Ð¾ÑÐ½Ð¸Ðµ
      setChildren(updatedChildren);
      
      handleCloseModal();
    }
  };

  const getStatusText = (status) => {
    const statusMap = {
      'mom': 'ÐœÐ°Ð¼Ð°',
      'pregnant': 'Ð‘ÐµÑ€ÐµÐ¼ÐµÐ½Ð½Ð°Ñ',
      'planning': 'ÐŸÐ»Ð°Ð½Ð¸Ñ€ÑƒÑŽ Ð±ÐµÑ€ÐµÐ¼ÐµÐ½Ð½Ð¾ÑÑ‚ÑŒ',
      'other': 'Ð”Ñ€ÑƒÐ³Ð¾Ðµ'
    };
    return statusMap[status] || status;
  };

  const getChildAvatar = (gender) => {
    return gender === 'boy' ? 'ðŸ‘¶' : 'ðŸ‘§';
  };

  const getChildAgeText = (age) => {
    const ageMap = {
      '0-1': '0-1 Ð³Ð¾Ð´',
      '1-2': '1-2 Ð³Ð¾Ð´Ð°',
      '2-3': '2-3 Ð³Ð¾Ð´Ð°',
      '3-4': '3-4 Ð³Ð¾Ð´Ð°',
      '4-5': '4-5 Ð»ÐµÑ‚',
      '5-6': '5-6 Ð»ÐµÑ‚',
      '6-7': '6-7 Ð»ÐµÑ‚',
      '7+': '7+ Ð»ÐµÑ‚'
    };
    return ageMap[age] || age;
  };

  const renderTabContent = () => {
    switch (activeTab) {
      case 'posts':
        return (
          <div className="tab-content">
                         <div className="content-filters">
               <div className="category-filter">
                 <select 
                   value={selectedCategory} 
                   onChange={(e) => setSelectedCategory(e.target.value)}
                 >
                   {profile.categories.map(category => (
                     <option key={category} value={category}>{category}</option>
                   ))}
                 </select>
               </div>
             </div>
            
            {loading ? (
              <div className="loading-state">
                <p>Ð—Ð°Ð³Ñ€ÑƒÐ·ÐºÐ° Ð¿Ð¾ÑÑ‚Ð¾Ð²...</p>
              </div>
            ) : posts.length === 0 ? (
              <div className="empty-state">
                <div className="empty-icon">
                  <FontAwesomeIcon icon={faPen} />
                </div>
                <h3>Ð’Ñ‹ Ð½Ðµ Ð½Ð°Ð¿Ð¸ÑÐ°Ð»Ð¸ ÐµÑ‰Ðµ Ð½Ð¸ Ð¾Ð´Ð½Ð¾Ð¹ Ð·Ð°Ð¿Ð¸ÑÐ¸</h3>
                <p>ÐŸÐ¾Ð´ÐµÐ»Ð¸Ñ‚ÐµÑÑŒ ÑÐ²Ð¾Ð¸Ð¼Ð¸ Ð¼Ñ‹ÑÐ»ÑÐ¼Ð¸ Ð¸ Ð¾Ð¿Ñ‹Ñ‚Ð¾Ð¼ Ñ Ð´Ñ€ÑƒÐ³Ð¸Ð¼Ð¸ Ð¼Ð°Ð¼Ð°Ð¼Ð¸</p>
                <button className="write-button" onClick={handleWritePost}>
                  <FontAwesomeIcon icon={faPen} />
                  ÐÐ°Ð¿Ð¸ÑÐ°Ñ‚ÑŒ Ð¿ÐµÑ€Ð²ÑƒÑŽ Ð·Ð°Ð¿Ð¸ÑÑŒ
                </button>
              </div>
            ) : (
              <div className="posts-list">
                {posts.map(post => (
                  <div key={post.id} className="post-card">
                    <div className="post-header">
                      <h4>{post.title}</h4>
                      <span className="post-date">
                        {new Date(post.created_at).toLocaleDateString('ru-RU')}
                      </span>
                    </div>
                    <p className="post-excerpt">{post.short_description}</p>
                    <div className="post-meta">
                      <span className="post-category">{post.category_name}</span>
                      <span className="post-status published">ÐžÐ¿ÑƒÐ±Ð»Ð¸ÐºÐ¾Ð²Ð°Ð½Ð¾</span>
                    </div>
                  </div>
                ))}
              </div>
            )}
          </div>
        );
      
      case 'drafts':
        return (
          <div className="tab-content">
            {loading ? (
              <div className="loading-state">
                <p>Ð—Ð°Ð³Ñ€ÑƒÐ·ÐºÐ° Ñ‡ÐµÑ€Ð½Ð¾Ð²Ð¸ÐºÐ¾Ð²...</p>
              </div>
            ) : drafts.length === 0 ? (
              <div className="empty-state">
                <div className="empty-icon">
                  <FontAwesomeIcon icon={faEdit} />
                </div>
                <h3>Ð£ Ð²Ð°Ñ Ð¿Ð¾ÐºÐ° Ð½ÐµÑ‚ Ñ‡ÐµÑ€Ð½Ð¾Ð²Ð¸ÐºÐ¾Ð²</h3>
                <p>Ð¡Ð¾Ð·Ð´Ð°Ð¹Ñ‚Ðµ Ñ‡ÐµÑ€Ð½Ð¾Ð²Ð¸Ðº, Ñ‡Ñ‚Ð¾Ð±Ñ‹ ÑÐ¾Ñ…Ñ€Ð°Ð½Ð¸Ñ‚ÑŒ Ð¸Ð´ÐµÐ¸ Ð´Ð»Ñ Ð±ÑƒÐ´ÑƒÑ‰Ð¸Ñ… Ð·Ð°Ð¿Ð¸ÑÐµÐ¹</p>
                <button className="write-button" onClick={handleWritePost}>
                  <FontAwesomeIcon icon={faPen} />
                  Ð¡Ð¾Ð·Ð´Ð°Ñ‚ÑŒ Ñ‡ÐµÑ€Ð½Ð¾Ð²Ð¸Ðº
                </button>
              </div>
            ) : (
              <div className="drafts-list">
                {drafts.map(draft => (
                  <div key={draft.id} className="draft-card">
                    <div className="draft-header">
                      <h4>{draft.title}</h4>
                      <span className="draft-date">
                        {new Date(draft.created_at).toLocaleDateString('ru-RU')}
                      </span>
                    </div>
                    <p className="draft-excerpt">{draft.short_description}</p>
                    <div className="draft-meta">
                      <span className="draft-category">{draft.category_name}</span>
                      <span className="draft-status">Ð§ÐµÑ€Ð½Ð¾Ð²Ð¸Ðº</span>
                    </div>
                    <div className="draft-actions">
                      <button 
                        className="publish-button"
                        onClick={() => handlePublishDraft(draft.id)}
                      >
                        <FontAwesomeIcon icon={faEye} />
                        ÐžÐ¿ÑƒÐ±Ð»Ð¸ÐºÐ¾Ð²Ð°Ñ‚ÑŒ
                      </button>
                    </div>
                  </div>
                ))}
              </div>
            )}
          </div>
        );
      
      case 'archive':
        return (
          <div className="tab-content">
            <div className="empty-state">
              <div className="empty-icon">
                <FontAwesomeIcon icon={faCog} />
              </div>
              <h3>ÐÑ€Ñ…Ð¸Ð² Ð¿ÑƒÑÑ‚</h3>
              <p>Ð—Ð´ÐµÑÑŒ Ð±ÑƒÐ´ÑƒÑ‚ Ñ…Ñ€Ð°Ð½Ð¸Ñ‚ÑŒÑÑ Ð²Ð°ÑˆÐ¸ ÑÑ‚Ð°Ñ€Ñ‹Ðµ Ð·Ð°Ð¿Ð¸ÑÐ¸</p>
            </div>
          </div>
        );
      
      default:
        return null;
    }
  };

  return (
    <div className="profile-page">
      {/* Main Content */}
      <main className="profile-main">
        {/* Profile Banner Ð² ÑÑ‚Ð¸Ð»Ðµ MomsBlog */}
        <div className="profile-banner">
          <div className="profile-avatar">
            {profile.personalInfo.avatar && profile.personalInfo.avatar !== 'null' ? (
              <img 
                src={profile.personalInfo.avatar} 
                alt={profile.fullName}
                onError={(e) => {
                  console.log('ÐžÑˆÐ¸Ð±ÐºÐ° Ð·Ð°Ð³Ñ€ÑƒÐ·ÐºÐ¸ Ð°Ð²Ð°Ñ‚Ð°Ñ€Ð°:', e.target.src);
                  e.target.style.display = 'none';
                  e.target.nextSibling.style.display = 'flex';
                }}
                onLoad={() => {
                  console.log('ÐÐ²Ð°Ñ‚Ð°Ñ€ ÑƒÑÐ¿ÐµÑˆÐ½Ð¾ Ð·Ð°Ð³Ñ€ÑƒÐ¶ÐµÐ½:', profile.personalInfo.avatar);
                }}
              />
            ) : null}
            <div className="avatar-placeholder" style={{ display: (profile.personalInfo.avatar && profile.personalInfo.avatar !== 'null') ? 'none' : 'flex' }}>
              {profile.avatar}
              {profile.isOnline && <span className="online-indicator"></span>}
            </div>
          </div>
          
          <div className="profile-info">
            <div className="profile-name">
              <h1>{profile.username}</h1>
            </div>
            <div className="profile-details">
              <div className="profile-location">
                <FontAwesomeIcon icon={faMapMarkerAlt} />
                {profile.location}
              </div>
              <div className="profile-age">
                <FontAwesomeIcon icon={faUser} />
                {profile.personalInfo.birthDate ? 
                  `${new Date().getFullYear() - new Date(profile.personalInfo.birthDate).getFullYear()} Ð»ÐµÑ‚` : 
                  'Ð’Ð¾Ð·Ñ€Ð°ÑÑ‚ Ð½Ðµ ÑƒÐºÐ°Ð·Ð°Ð½'
                }
              </div>
              <div className="profile-status">
                <FontAwesomeIcon icon={faBaby} />
                {getStatusText(profile.personalInfo.status)}
              </div>
            </div>
                         <div className="profile-actions">
               <button className="edit-profile-btn" onClick={handleEditProfile}>
                 <FontAwesomeIcon icon={faEdit} />
                 Ð ÐµÐ´Ð°ÐºÑ‚Ð¸Ñ€Ð¾Ð²Ð°Ñ‚ÑŒ Ð¿Ñ€Ð¾Ñ„Ð¸Ð»ÑŒ
               </button>
             </div>
          </div>
        </div>

        {/* Statistics */}
        <div className="profile-stats">
          <div className="stat-item">
            <span className="stat-number">{profile.statistics.posts}</span>
            <span className="stat-label">Ð·Ð°Ð¿Ð¸ÑÐµÐ¹</span>
          </div>
          <div className="stat-item">
            <span className="stat-number">{profile.statistics.friends}</span>
            <span className="stat-label">Ð¿Ð¾Ð´Ð¿Ð¸ÑÐ¾Ðº</span>
          </div>
          <div className="stat-item">
            <span className="stat-number">{profile.statistics.subscribers}</span>
            <span className="stat-label">Ð¿Ð¾Ð´Ð¿Ð¸ÑÑ‡Ð¸ÐºÐ¾Ð²</span>
          </div>
        </div>

        {/* Add Child Button */}
        <div className="add-child-section">
          {children.length > 0 && (
            <div className="children-list">
              {children.map((child) => (
                <div key={child.id} className="child-card">
                  <div className="child-avatar">
                    <span className="child-emoji">{getChildAvatar(child.gender)}</span>
                  </div>
                  <div className="child-info">
                    <div className="child-name">{child.name}</div>
                    <div className="child-age">{getChildAgeText(child.age)}</div>
                  </div>
                </div>
              ))}
            </div>
          )}
          <button className="add-child-btn" onClick={handleAddChild}>
            <FontAwesomeIcon icon={faPlus} />
            Ð”Ð¾Ð±Ð°Ð²Ð¸Ñ‚ÑŒ Ñ€ÐµÐ±ÐµÐ½ÐºÐ°
          </button>
        </div>

        {/* Tabs */}
        <div className="profile-tabs">
          <button
            className={`tab-button ${activeTab === 'posts' ? 'active' : ''}`}
            onClick={() => handleTabChange('posts')}
          >
            Ð—Ð°Ð¿Ð¸ÑÐ¸
          </button>
          <button
            className={`tab-button ${activeTab === 'drafts' ? 'active' : ''}`}
            onClick={() => handleTabChange('drafts')}
          >
            Ð§ÐµÑ€Ð½Ð¾Ð²Ð¸ÐºÐ¸
          </button>
          <button
            className={`tab-button ${activeTab === 'archive' ? 'active' : ''}`}
            onClick={() => handleTabChange('archive')}
          >
            ÐÑ€Ñ…Ð¸Ð²
          </button>
        </div>

        {/* Tab Content */}
        {renderTabContent()}
