import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import {
  faPen,
  faUser,
  faBaby,
  faEdit,
  faCog,
  faPlus,
  faTimes,
  faMapMarkerAlt,
  faEye
} from '@fortawesome/free-solid-svg-icons';
import profileData from '../../data/profileData.json';
import './ProfilePage.css';

const ProfilePage = () => {
  const { userId } = useParams();
  const navigate = useNavigate();
  const [profile, setProfile] = useState(profileData.profile);
  const [activeTab, setActiveTab] = useState('posts');
  const [selectedCategory, setSelectedCategory] = useState('Все категории');
  const [showAddChildModal, setShowAddChildModal] = useState(false);
  const [posts, setPosts] = useState([]);
  const [drafts, setDrafts] = useState([]);
  const [loading, setLoading] = useState(false);
  const [showEditProfileModal, setShowEditProfileModal] = useState(false);
  const [childData, setChildData] = useState({
    name: '',
    gender: '',
    age: ''
  });
  const [editProfileData, setEditProfileData] = useState({
    city: '',
    birthDate: '',
    status: ''
  });
  const [children, setChildren] = useState([]);

  useEffect(() => {
    // Получаем данные пользователя из localStorage (если есть)
    const userData = localStorage.getItem('userData');
    if (userData) {
      const user = JSON.parse(userData);
      console.log('Загруженные данные пользователя:', user);
      console.log('Аватар пользователя:', user.avatar);
      
      setProfile(prev => ({
        ...prev,
        username: user.first_name || user.username || prev.username,
        fullName: `${user.first_name || ''} ${user.last_name || ''}`.trim(),
        avatar: user.first_name ? `${user.first_name[0]}${user.last_name ? user.last_name[0] : ''}` : prev.avatar,
        location: user.city || prev.location,
        personalInfo: {
          ...prev.personalInfo,
          firstName: user.first_name || prev.personalInfo.firstName,
          lastName: user.last_name || prev.personalInfo.lastName,
          email: user.email || prev.personalInfo.email,
          status: user.status || prev.personalInfo.status,
          city: user.city || prev.personalInfo.city,
          birthDate: user.birth_date || prev.personalInfo.birthDate,
          avatar: user.avatar || prev.personalInfo.avatar
        }
      }));

      // Инициализируем данные для редактирования профиля
      setEditProfileData({
        city: user.city || '',
        birthDate: user.birth_date || '',
        status: user.status || ''
      });

      // Загружаем детей из localStorage
      const savedChildren = JSON.parse(localStorage.getItem('children') || '[]');
      setChildren(savedChildren);
    }
    
    // Загружаем посты пользователя
    fetchUserPosts();
  }, [userId]);

  const fetchUserPosts = async () => {
    setLoading(true);
    try {
      const token = localStorage.getItem('accessToken') || sessionStorage.getItem('accessToken');
      const response = await fetch('http://localhost:8000/api/posts/user-posts/', {
        headers: {
          'Authorization': `Bearer ${token}`
        }
      });
      
      if (response.ok) {
        const data = await response.json();
        const publishedPosts = data.posts.filter(post => post.status === 'published');
        const draftPosts = data.posts.filter(post => post.status === 'draft');
        
        setPosts(publishedPosts);
        setDrafts(draftPosts);
      } else {
        console.error('Ошибка загрузки постов:', response.status);
      }
    } catch (error) {
      console.error('Ошибка загрузки постов:', error);
    } finally {
      setLoading(false);
    }
  };

  const handleTabChange = (tabId) => {
    setActiveTab(tabId);
  };

  const handleWritePost = () => {
    navigate('/create-post');
  };

  const handlePublishDraft = async (draftId) => {
    try {
      const token = localStorage.getItem('accessToken') || sessionStorage.getItem('accessToken');
      const response = await fetch(`http://localhost:8000/api/posts/${draftId}/publish/`, {
        method: 'PATCH',
        headers: {
          'Authorization': `Bearer ${token}`,
          'Content-Type': 'application/json'
        },
        body: JSON.stringify({ status: 'published' })
      });
      
      if (response.ok) {
        // Обновляем списки постов и черновиков
        await fetchUserPosts();
        alert('Черновик успешно опубликован!');
      } else {
        alert('Ошибка при публикации черновика');
      }
    } catch (error) {
      console.error('Ошибка публикации черновика:', error);
      alert('Произошла ошибка при публикации');
    }
  };

  const handleEditProfile = () => {
    setShowEditProfileModal(true);
  };

  const handleAddChild = () => {
    setShowAddChildModal(true);
  };

  const handleCloseModal = () => {
    setShowAddChildModal(false);
    setShowEditProfileModal(false);
    setChildData({ name: '', gender: '', age: '' });
  };

  const handleEditProfileDataChange = (field, value) => {
    setEditProfileData(prev => ({
      ...prev,
      [field]: value
    }));
  };

  const handleSaveProfile = () => {
    // Обновляем данные в localStorage
    const userData = JSON.parse(localStorage.getItem('userData') || '{}');
    const updatedUserData = {
      ...userData,
      city: editProfileData.city,
      birth_date: editProfileData.birthDate,
      status: editProfileData.status
    };
    localStorage.setItem('userData', JSON.stringify(updatedUserData));

    // Обновляем состояние профиля
    setProfile(prev => ({
      ...prev,
      location: editProfileData.city,
      personalInfo: {
        ...prev.personalInfo,
        city: editProfileData.city,
        birthDate: editProfileData.birthDate,
        status: editProfileData.status
      }
    }));

    // Закрываем модальное окно
    setShowEditProfileModal(false);
  };

  const handleChildDataChange = (field, value) => {
    setChildData(prev => ({
      ...prev,
      [field]: value
    }));
  };

  const handleSaveChild = () => {
    if (childData.name && childData.gender && childData.age) {
      // Здесь можно сохранить данные ребенка в localStorage или отправить на сервер
      console.log('Данные ребенка:', childData);
      
      // Сохраняем в localStorage для демонстрации
      const existingChildren = JSON.parse(localStorage.getItem('children') || '[]');
      const newChild = {
        id: Date.now(),
        ...childData
      };
      const updatedChildren = [...existingChildren, newChild];
      localStorage.setItem('children', JSON.stringify(updatedChildren));
      
      // Обновляем состояние
      setChildren(updatedChildren);
      
      handleCloseModal();
    }
  };

  const getStatusText = (status) => {
    const statusMap = {
      'mom': 'Мама',
      'pregnant': 'Беременная',
      'planning': 'Планирую беременность',
      'other': 'Другое'
    };
    return statusMap[status] || status;
  };

  const getChildAvatar = (gender) => {
    return gender === 'boy' ? '👶' : '👧';
  };

  const getChildAgeText = (age) => {
    const ageMap = {
      '0-1': '0-1 год',
      '1-2': '1-2 года',
      '2-3': '2-3 года',
      '3-4': '3-4 года',
      '4-5': '4-5 лет',
      '5-6': '5-6 лет',
      '6-7': '6-7 лет',
      '7+': '7+ лет'
    };
    return ageMap[age] || age;
  };

  const renderTabContent = () => {
    switch (activeTab) {
      case 'posts':
        return (
          <div className="tab-content">
                         <div className="content-filters">
               <div className="category-filter">
                 <select 
                   value={selectedCategory} 
                   onChange={(e) => setSelectedCategory(e.target.value)}
                 >
                   {profile.categories.map(category => (
                     <option key={category} value={category}>{category}</option>
                   ))}
                 </select>
               </div>
             </div>
            
            {loading ? (
              <div className="loading-state">
                <p>Загрузка постов...</p>
              </div>
            ) : posts.length === 0 ? (
              <div className="empty-state">
                <div className="empty-icon">
                  <FontAwesomeIcon icon={faPen} />
                </div>
                <h3>Вы не написали еще ни одной записи</h3>
                <p>Поделитесь своими мыслями и опытом с другими мамами</p>
                <button className="write-button" onClick={handleWritePost}>
                  <FontAwesomeIcon icon={faPen} />
                  Написать первую запись
                </button>
              </div>
            ) : (
              <div className="posts-list">
                {posts.map(post => (
                  <div key={post.id} className="post-card">
                    <div className="post-header">
                      <h4>{post.title}</h4>
                      <span className="post-date">
                        {new Date(post.created_at).toLocaleDateString('ru-RU')}
                      </span>
                    </div>
                    <p className="post-excerpt">{post.short_description}</p>
                    <div className="post-meta">
                      <span className="post-category">{post.category_name}</span>
                      <span className="post-status published">Опубликовано</span>
                    </div>
                  </div>
                ))}
              </div>
            )}
          </div>
        );
      
      case 'drafts':
        return (
          <div className="tab-content">
            {loading ? (
              <div className="loading-state">
                <p>Загрузка черновиков...</p>
              </div>
            ) : drafts.length === 0 ? (
              <div className="empty-state">
                <div className="empty-icon">
                  <FontAwesomeIcon icon={faEdit} />
                </div>
                <h3>У вас пока нет черновиков</h3>
                <p>Создайте черновик, чтобы сохранить идеи для будущих записей</p>
                <button className="write-button" onClick={handleWritePost}>
                  <FontAwesomeIcon icon={faPen} />
                  Создать черновик
                </button>
              </div>
            ) : (
              <div className="drafts-list">
                {drafts.map(draft => (
                  <div key={draft.id} className="draft-card">
                    <div className="draft-header">
                      <h4>{draft.title}</h4>
                      <span className="draft-date">
                        {new Date(draft.created_at).toLocaleDateString('ru-RU')}
                      </span>
                    </div>
                    <p className="draft-excerpt">{draft.short_description}</p>
                    <div className="draft-meta">
                      <span className="draft-category">{draft.category_name}</span>
                      <span className="draft-status">Черновик</span>
                    </div>
                    <div className="draft-actions">
                      <button 
                        className="publish-button"
                        onClick={() => handlePublishDraft(draft.id)}
                      >
                        <FontAwesomeIcon icon={faEye} />
                        Опубликовать
                      </button>
                    </div>
                  </div>
                ))}
              </div>
            )}
          </div>
        );
      
      case 'archive':
        return (
          <div className="tab-content">
            <div className="empty-state">
              <div className="empty-icon">
                <FontAwesomeIcon icon={faCog} />
              </div>
              <h3>Архив пуст</h3>
              <p>Здесь будут храниться ваши старые записи</p>
            </div>
          </div>
        );
      
      default:
        return null;
    }
  };

  return (
    <div className="profile-page">
      {/* Main Content */}
      <main className="profile-main">
        {/* Profile Banner в стиле MomsBlog */}
        <div className="profile-banner">
          <div className="profile-avatar">
            {profile.personalInfo.avatar && profile.personalInfo.avatar !== 'null' ? (
              <img 
                src={profile.personalInfo.avatar} 
                alt={profile.fullName}
                onError={(e) => {
                  console.log('Ошибка загрузки аватара:', e.target.src);
                  e.target.style.display = 'none';
                  e.target.nextSibling.style.display = 'flex';
                }}
                onLoad={() => {
                  console.log('Аватар успешно загружен:', profile.personalInfo.avatar);
                }}
              />
            ) : null}
            <div className="avatar-placeholder" style={{ display: (profile.personalInfo.avatar && profile.personalInfo.avatar !== 'null') ? 'none' : 'flex' }}>
              {profile.avatar}
              {profile.isOnline && <span className="online-indicator"></span>}
            </div>
          </div>
          
          <div className="profile-info">
            <div className="profile-name">
              <h1>{profile.username}</h1>
            </div>
            <div className="profile-details">
              <div className="profile-location">
                <FontAwesomeIcon icon={faMapMarkerAlt} />
                {profile.location}
              </div>
              <div className="profile-age">
                <FontAwesomeIcon icon={faUser} />
                {profile.personalInfo.birthDate ? 
                  `${new Date().getFullYear() - new Date(profile.personalInfo.birthDate).getFullYear()} лет` : 
                  'Возраст не указан'
                }
              </div>
              <div className="profile-status">
                <FontAwesomeIcon icon={faBaby} />
                {getStatusText(profile.personalInfo.status)}
              </div>
            </div>
                         <div className="profile-actions">
               <button className="edit-profile-btn" onClick={handleEditProfile}>
                 <FontAwesomeIcon icon={faEdit} />
                 Редактировать профиль
               </button>
             </div>
          </div>
        </div>

        {/* Statistics */}
        <div className="profile-stats">
          <div className="stat-item">
            <span className="stat-number">{profile.statistics.posts}</span>
            <span className="stat-label">записей</span>
          </div>
          <div className="stat-item">
            <span className="stat-number">{profile.statistics.friends}</span>
            <span className="stat-label">подписок</span>
          </div>
          <div className="stat-item">
            <span className="stat-number">{profile.statistics.subscribers}</span>
            <span className="stat-label">подписчиков</span>
          </div>
        </div>

        {/* Add Child Button */}
        <div className="add-child-section">
          {children.length > 0 && (
            <div className="children-list">
              {children.map((child) => (
                <div key={child.id} className="child-card">
                  <div className="child-avatar">
                    <span className="child-emoji">{getChildAvatar(child.gender)}</span>
                  </div>
                  <div className="child-info">
                    <div className="child-name">{child.name}</div>
                    <div className="child-age">{getChildAgeText(child.age)}</div>
                  </div>
                </div>
              ))}
            </div>
          )}
          <button className="add-child-btn" onClick={handleAddChild}>
            <FontAwesomeIcon icon={faPlus} />
            Добавить ребенка
          </button>
        </div>

        {/* Tabs */}
        <div className="profile-tabs">
          <button
            className={`tab-button ${activeTab === 'posts' ? 'active' : ''}`}
            onClick={() => handleTabChange('posts')}
          >
            Записи
          </button>
          <button
            className={`tab-button ${activeTab === 'drafts' ? 'active' : ''}`}
            onClick={() => handleTabChange('drafts')}
          >
            Черновики
          </button>
          <button
            className={`tab-button ${activeTab === 'archive' ? 'active' : ''}`}
            onClick={() => handleTabChange('archive')}
          >
            Архив
          </button>
        </div>

        {/* Tab Content */}
        {renderTabContent()}
      </main>

      {/* Add Child Modal */}
      {showAddChildModal && (
        <div className="modal-overlay" onClick={handleCloseModal}>
          <div className="modal-content" onClick={(e) => e.stopPropagation()}>
            <div className="modal-header">
              <h3>Добавить ребенка</h3>
              <button className="modal-close" onClick={handleCloseModal}>
                <FontAwesomeIcon icon={faTimes} />
              </button>
            </div>
            
            <div className="modal-body">
              <div className="form-group">
                <label>Имя ребенка</label>
                <input
                  type="text"
                  placeholder="Введите имя"
                  value={childData.name}
                  onChange={(e) => handleChildDataChange('name', e.target.value)}
                />
              </div>
              
              <div className="form-group">
                <label>Пол</label>
                <select
                  value={childData.gender}
                  onChange={(e) => handleChildDataChange('gender', e.target.value)}
                >
                  <option value="">Выберите пол</option>
                  <option value="boy">Мальчик</option>
                  <option value="girl">Девочка</option>
                </select>
              </div>
              
              <div className="form-group">
                <label>Возраст</label>
                <select
                  value={childData.age}
                  onChange={(e) => handleChildDataChange('age', e.target.value)}
                >
                  <option value="">Выберите возраст</option>
                  <option value="0-1">0-1 год</option>
                  <option value="1-2">1-2 года</option>
                  <option value="2-3">2-3 года</option>
                  <option value="3-4">3-4 года</option>
                  <option value="4-5">4-5 лет</option>
                  <option value="5-6">5-6 лет</option>
                  <option value="6-7">6-7 лет</option>
                  <option value="7+">7+ лет</option>
                </select>
              </div>
            </div>
            
            <div className="modal-footer">
              <button className="btn-cancel" onClick={handleCloseModal}>
                Отмена
              </button>
              <button 
                className="btn-save" 
                onClick={handleSaveChild}
                disabled={!childData.name || !childData.gender || !childData.age}
              >
                Сохранить
              </button>
            </div>
          </div>
        </div>
      )}

      {/* Edit Profile Modal */}
      {showEditProfileModal && (
        <div className="modal-overlay" onClick={handleCloseModal}>
          <div className="modal-content" onClick={(e) => e.stopPropagation()}>
            <div className="modal-header">
              <h3>Редактировать профиль</h3>
              <button className="modal-close" onClick={handleCloseModal}>
                <FontAwesomeIcon icon={faTimes} />
              </button>
            </div>
            
            <div className="modal-body">
              <div className="form-group">
                <label>Город</label>
                <input
                  type="text"
                  placeholder="Введите город"
                  value={editProfileData.city}
                  onChange={(e) => handleEditProfileDataChange('city', e.target.value)}
                />
              </div>
              
              <div className="form-group">
                <label>Дата рождения</label>
                <input
                  type="date"
                  value={editProfileData.birthDate}
                  onChange={(e) => handleEditProfileDataChange('birthDate', e.target.value)}
                />
              </div>
              
              <div className="form-group">
                <label>Статус</label>
                <select
                  value={editProfileData.status}
                  onChange={(e) => handleEditProfileDataChange('status', e.target.value)}
                >
                  <option value="">Выберите статус</option>
                  <option value="mom">Мама</option>
                  <option value="pregnant">Беременная</option>
                  <option value="planning">Планирую беременность</option>
                  <option value="other">Другое</option>
                </select>
              </div>
            </div>
            
            <div className="modal-footer">
              <button className="btn-cancel" onClick={handleCloseModal}>
                Отмена
              </button>
              <button 
                className="btn-save" 
                onClick={handleSaveProfile}
                disabled={!editProfileData.city || !editProfileData.birthDate || !editProfileData.status}
              >
                Сохранить
              </button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default ProfilePage;
