import React from 'react';

const SimpleModal = ({ isOpen, onClose, message }) => {
  console.log('SimpleModal render:', { isOpen, message: !!message });
  
  if (!isOpen || !message) {
    console.log('SimpleModal: не рендерится');
    return null;
  }
  
  console.log('SimpleModal: рендерится');
  
  return (
    <div style={{
      position: 'fixed',
      top: 0,
      left: 0,
      right: 0,
      bottom: 0,
      backgroundColor: 'rgba(0, 0, 0, 0.5)',
      display: 'flex',
      alignItems: 'center',
      justifyContent: 'center',
      zIndex: 1000
    }} onClick={onClose}>
      <div style={{
        backgroundColor: 'white',
        padding: '20px',
        borderRadius: '8px',
        maxWidth: '400px',
        width: '90%'
      }} onClick={(e) => e.stopPropagation()}>
        <h3>Действия с сообщением</h3>
        <p>Сообщение: "{message.content}"</p>
        <button onClick={onClose}>Закрыть</button>
      </div>
    </div>
  );
};

export default SimpleModal;
