import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { 
  faBookmark, 
  faUser, 
  faMapMarkerAlt, 
  faCalendar,
  faArrowLeft
} from '@fortawesome/free-solid-svg-icons';
import './ArchivePage.css';

const ArchivePage = () => {
  const [archivedPosts, setArchivedPosts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    // Отладочная информация о пользователе
    const userData = localStorage.getItem('userData') || sessionStorage.getItem('userData');
    if (userData) {
      try {
        const user = JSON.parse(userData);
        console.log('👤 Текущий пользователь в ArchivePage:', user);
        
        // Проверяем, что пользователь "владиславмелевич"
        if (user.username === 'владиславмелевич') {
          console.log('✅ Правильный пользователь!');
        } else {
          console.log('❌ Неправильный пользователь! Нужно войти под "владиславмелевич"');
        }
      } catch (e) {
        console.log('❌ Ошибка парсинга данных пользователя:', e);
      }
    } else {
      console.log('❌ Данные пользователя не найдены в ArchivePage');
    }
    
    fetchArchivedPosts();
  }, []);

  const fetchArchivedPosts = async () => {
    try {
      setLoading(true);
      const token = localStorage.getItem('accessToken') || sessionStorage.getItem('accessToken');
      
      // Проверяем токен и получаем информацию о пользователе
      if (token) {
        try {
          const userResponse = await fetch('http://localhost:8000/api/users/profile/', {
            headers: {
              'Authorization': `Bearer ${token}`,
              'Content-Type': 'application/json'
            }
          });
          
          if (userResponse.ok) {
            const userData = await userResponse.json();
            console.log('👤 Текущий авторизованный пользователь:', userData);
          } else {
            console.log('❌ Ошибка получения профиля пользователя:', userResponse.status);
          }
        } catch (error) {
          console.log('❌ Ошибка при проверке пользователя:', error);
        }
      }
      
      console.log('🔍 Загрузка архива...');
      console.log('🔑 Токен:', token ? `Есть (${token.substring(0, 20)}...)` : 'Нет');
      
      // Проверяем данные пользователя
      const userData = localStorage.getItem('userData') || sessionStorage.getItem('userData');
      if (userData) {
        try {
          const user = JSON.parse(userData);
          console.log('👤 Текущий пользователь:', user);
        } catch (e) {
          console.log('❌ Ошибка парсинга данных пользователя:', e);
        }
      } else {
        console.log('❌ Данные пользователя не найдены');
      }
      
      if (!token) {
        setError('Необходима авторизация');
        setLoading(false);
        return;
      }

      console.log('🔗 Отправляем запрос к API архива...');
      const response = await fetch('http://localhost:8000/api/users/archive/', {
        headers: {
          'Authorization': `Bearer ${token}`,
          'Content-Type': 'application/json'
        }
      });

      console.log('Статус ответа:', response.status);
      console.log('Заголовки ответа:', response.headers);

      if (response.ok) {
        const data = await response.json();
        console.log('✅ Данные от API:', data);
        console.log('📊 Количество постов:', data.results ? data.results.length : 0);
        
        if (data.results && data.results.length > 0) {
          console.log('📋 Посты в архиве:');
          data.results.forEach((post, index) => {
            console.log(`  ${index + 1}. "${post.title}" (ID: ${post.id})`);
            console.log(`     Автор: ${post.author?.username || 'Неизвестно'}`);
          });
        } else {
          console.log('📭 Архив пуст или данные не получены');
        }
        
        setArchivedPosts(data.results || []);
      } else {
        const errorText = await response.text();
        console.error('❌ Ошибка API:', response.status, errorText);
        setError('Ошибка загрузки архива');
      }
    } catch (error) {
      console.error('Ошибка загрузки архива:', error);
      setError('Ошибка загрузки архива');
    } finally {
      setLoading(false);
    }
  };

  const formatDate = (dateString) => {
    try {
      const date = new Date(dateString);
      return date.toLocaleDateString('ru-RU', {
        year: 'numeric',
        month: 'long',
        day: 'numeric'
      });
    } catch (error) {
      return 'Недавно';
    }
  };

  const getAuthorInitials = (author) => {
    if (!author) return 'А';
    const firstName = author.first_name || '';
    const lastName = author.last_name || '';
    return `${firstName.charAt(0)}${lastName.charAt(0)}`.toUpperCase() || 'А';
  };

  const getUserAvatarUrl = (userData) => {
    if (userData.avatar) return userData.avatar;
    const initials = getAuthorInitials(userData);
    return `https://via.placeholder.com/40x40/667eea/ffffff?text=${encodeURIComponent(initials)}`;
  };

  if (loading) {
    return (
      <div className="archive-loading">
        <div className="loading-spinner"></div>
        <p>Загрузка архива...</p>
      </div>
    );
  }

  if (error) {
    return (
      <div className="archive-error">
        <h2>Ошибка</h2>
        <p>{error}</p>
        <button onClick={fetchArchivedPosts} className="btn-retry">
          Попробовать снова
        </button>
      </div>
    );
  }

  return (
    <div className="archive-page">
      {/* Хлебные крошки */}
      <div className="breadcrumbs-container">
        <nav className="breadcrumbs">
          <Link to="/" className="breadcrumb-link">Главная</Link>
          <FontAwesomeIcon icon={faArrowLeft} className="breadcrumb-separator" />
          <span className="breadcrumb-link active">Архив</span>
        </nav>
      </div>

      {/* Заголовок */}
      <div className="archive-header">
        <div className="archive-title">
          <FontAwesomeIcon icon={faBookmark} className="archive-icon" />
          <h1>Мой архив</h1>
        </div>
        <p className="archive-description">
          Здесь собраны все посты, которые вы сохранили для себя
        </p>
        <div className="debug-info">
          <p><strong>Отладка:</strong> Проверьте консоль браузера (F12) для подробной информации</p>
        </div>
        <button onClick={fetchArchivedPosts} className="refresh-btn">
          Обновить архив
        </button>
        <button onClick={() => {
          // Очищаем данные пользователя и перенаправляем на логин
          localStorage.removeItem('userData');
          localStorage.removeItem('accessToken');
          sessionStorage.removeItem('userData');
          sessionStorage.removeItem('accessToken');
          window.location.href = '/login';
        }} className="login-btn">
          Войти под владиславмелевич
        </button>
        <button onClick={() => {
          // Принудительно устанавливаем данные пользователя владиславмелевич
          const userData = {
            id: 5,
            username: 'владиславмелевич',
            first_name: 'Владислав',
            last_name: 'Мелевич',
            email: 'vladislav@example.com'
          };
          localStorage.setItem('userData', JSON.stringify(userData));
          // Обновляем страницу
          window.location.reload();
        }} className="force-login-btn">
          Принудительно войти как владиславмелевич
        </button>
      </div>

      {/* Список постов */}
      <div className="archive-content">
        {archivedPosts.length === 0 ? (
          <div className="archive-empty">
            <FontAwesomeIcon icon={faBookmark} className="empty-icon" />
            <h3>Архив пуст</h3>
            <p>Вы пока не сохранили ни одного поста в архив</p>
            <Link to="/posts" className="btn-browse-posts">
              Просмотреть посты
            </Link>
          </div>
        ) : (
          <div className="archived-posts-grid">
            {archivedPosts.map((post) => (
              <div key={post.id} className="archived-post-card">
                <div className="post-header">
                  <div className="author-info">
                    <div className="author-avatar">
                      {post.author.avatar ? (
                        <img 
                          src={post.author.avatar} 
                          alt={post.author.first_name}
                          onError={(e) => {
                            e.target.style.display = 'none';
                            e.target.nextSibling.style.display = 'flex';
                          }}
                        />
                      ) : null}
                      <div className="author-initials" style={{ 
                        display: post.author.avatar ? 'none' : 'flex' 
                      }}>
                        {getAuthorInitials(post.author)}
                      </div>
                    </div>
                    <div className="author-details">
                      <div className="author-name">
                        {post.author.first_name} {post.author.last_name}
                      </div>
                      <div className="post-meta">
                        <span className="post-time">
                          <FontAwesomeIcon icon={faCalendar} />
                          {formatDate(post.created_at)}
                        </span>
                        <span className="post-location">
                          <FontAwesomeIcon icon={faMapMarkerAlt} />
                          {post.author.city || 'Не указан'}
                        </span>
                      </div>
                    </div>
                  </div>
                </div>
                
                <div className="post-content">
                  <h3 className="post-title">
                    <Link to={`/post-detail/${post.slug}`}>
                      {post.title}
                    </Link>
                  </h3>
                  
                  {post.category && (
                    <div className="post-category">
                      {post.category.name}
                    </div>
                  )}
                  
                  {post.short_description && (
                    <p className="post-excerpt">
                      {post.short_description}
                    </p>
                  )}
                  
                  <div className="post-actions">
                    <Link to={`/post-detail/${post.slug}`} className="read-more">
                      Читать далее
                    </Link>
                    <span className="archived-date">
                      Сохранено: {formatDate(post.archived_at)}
                    </span>
                  </div>
                </div>
              </div>
            ))}
          </div>
        )}
      </div>
    </div>
  );
};

export default ArchivePage;
