import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { 
  faArrowLeft, 
  faSearch, 
  faUser,
  faCircle,
  faUserFriends,
  faMessage,
  faClock,
  faEllipsisV,
  faPlus,
  faFilter
} from '@fortawesome/free-solid-svg-icons';
import './ChatsPage.css';

const ChatsPage = () => {
  const navigate = useNavigate();
  const [chats, setChats] = useState([]);
  const [loading, setLoading] = useState(true);
  const [searchQuery, setSearchQuery] = useState('');
  const [filteredChats, setFilteredChats] = useState([]);


  useEffect(() => {
    fetchChats();
  }, []);

  useEffect(() => {
    // Фильтрация чатов по поисковому запросу и фильтру
    let filtered = chats.filter(chat => {
      const matchesSearch = 
        chat.user?.first_name?.toLowerCase().includes(searchQuery.toLowerCase()) ||
        chat.user?.last_name?.toLowerCase().includes(searchQuery.toLowerCase()) ||
        chat.user?.username?.toLowerCase().includes(searchQuery.toLowerCase()) ||
        chat.lastMessage?.toLowerCase().includes(searchQuery.toLowerCase());
      
      if (activeFilter === 'unread') {
        return matchesSearch && chat.unreadCount > 0;
      } else if (activeFilter === 'online') {
        return matchesSearch && chat.isOnline;
      }
      
      return matchesSearch;
    });
    
    setFilteredChats(filtered);
  }, [searchQuery, chats, activeFilter]);

  const fetchChats = async () => {
    try {
      setLoading(true);
      const token = localStorage.getItem('accessToken') || sessionStorage.getItem('accessToken');
      
      if (!token) {
        navigate('/auth');
        return;
      }

      const response = await fetch('http://localhost:8000/api/users/chats/', {
        headers: {
          'Authorization': `Bearer ${token}`,
          'Content-Type': 'application/json',
        }
      });

      if (response.ok) {
        const data = await response.json();
        setChats(data.chats || []);
      } else {
        console.error('Ошибка загрузки чатов:', response.status);
        setChats(getMockChats());
      }
    } catch (error) {
      console.error('Ошибка загрузки чатов:', error);
      setChats(getMockChats());
    } finally {
      setLoading(false);
    }
  };

  const getMockChats = () => {
    return [
      {
        id: 1,
        user: {
          id: 2,
          first_name: 'Анна',
          last_name: 'Петрова',
          username: 'anna_p',
          avatar: null,
          city: 'Москва'
        },
        lastMessage: 'Привет! Как дела? Очень хочу обсудить новую статью',
        lastMessageTime: '2024-01-15T10:30:00Z',
        unreadCount: 2,
        isOnline: true
      },
      {
        id: 2,
        user: {
          id: 3,
          first_name: 'Мария',
          last_name: 'Иванова',
          username: 'maria_i',
          avatar: null,
          city: 'Санкт-Петербург'
        },
        lastMessage: 'Спасибо за совет! Очень помогло',
        lastMessageTime: '2024-01-15T09:15:00Z',
        unreadCount: 0,
        isOnline: false
      },
      {
        id: 3,
        user: {
          id: 4,
          first_name: 'Елена',
          last_name: 'Сидорова',
          username: 'elena_s',
          avatar: null,
          city: 'Казань'
        },
        lastMessage: 'Когда встретимся? Хочу показать тебе что-то интересное',
        lastMessageTime: '2024-01-14T18:45:00Z',
        unreadCount: 1,
        isOnline: true
      },
      {
        id: 4,
        user: {
          id: 5,
          first_name: 'Дмитрий',
          last_name: 'Козлов',
          username: 'dmitry_k',
          avatar: null,
          city: 'Новосибирск'
        },
        lastMessage: 'Отличная идея! Давайте обсудим детали',
        lastMessageTime: '2024-01-14T15:20:00Z',
        unreadCount: 0,
        isOnline: false
      },
      {
        id: 5,
        user: {
          id: 6,
          first_name: 'Ольга',
          last_name: 'Морозова',
          username: 'olga_m',
          avatar: null,
          city: 'Екатеринбург'
        },
        lastMessage: 'Согласна с твоим мнением по этому вопросу',
        lastMessageTime: '2024-01-14T12:10:00Z',
        unreadCount: 3,
        isOnline: true
      }
    ];
  };

  const handleChatClick = (chatId) => {
    navigate(`/chat/${chatId}`);
  };

  const getAuthorInitials = (user) => {
    if (!user) return 'А';
    const firstName = user.first_name || '';
    const lastName = user.last_name || '';
    return `${firstName.charAt(0)}${lastName.charAt(0)}`.toUpperCase() || 'А';
  };

  const getAvatarUrl = (user) => {
    if (!user) return null;
    
    if (user.avatar && user.avatar !== 'null' && user.avatar !== '') {
      if (user.avatar.startsWith('/')) {
        return `http://localhost:8000${user.avatar}`;
      }
      return user.avatar;
    }
    return null;
  };

  const formatTime = (dateString) => {
    if (!dateString) return '';
    
    const date = new Date(dateString);
    const now = new Date();
    const diffInHours = Math.floor((now - date) / (1000 * 60 * 60));
    
    if (diffInHours < 1) return 'Только что';
    if (diffInHours < 24) return `${diffInHours} ч назад`;
    
    const diffInDays = Math.floor(diffInHours / 24);
    if (diffInDays < 7) return `${diffInDays} дн назад`;
    
    return date.toLocaleDateString('ru-RU');
  };

  const getUserName = (user) => {
    if (!user) return 'Пользователь';
    const firstName = user.first_name || '';
    const lastName = user.last_name || '';
    const fullName = `${firstName} ${lastName}`.trim();
    return fullName || user.username || 'Пользователь';
  };

  const getUnreadCount = () => {
    return chats.reduce((total, chat) => total + chat.unreadCount, 0);
  };

  const getOnlineCount = () => {
    return chats.filter(chat => chat.isOnline).length;
  };

  if (loading) {
    return (
      <div className="chats-page">
        <div className="chats-loading">
          <div className="loading-container">
            <div className="loading-spinner"></div>
            <h3>Загружаем чаты...</h3>
            <p>Подключаемся к серверу сообщений</p>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="chats-page">
      {/* Хлебные крошки */}
      <div className="breadcrumbs-container">
        <nav className="breadcrumbs">
          <button className="breadcrumb-link" onClick={() => navigate('/')}>
            <FontAwesomeIcon icon={faArrowLeft} />
            Главная
          </button>
          <span className="breadcrumb-separator">/</span>
          <span className="breadcrumb-link active">Чаты</span>
        </nav>
      </div>

      {/* Заголовок */}
      <div className="chats-header">
        <div className="header-content">
          <div className="header-title">
            <div className="title-icon-wrapper">
              <FontAwesomeIcon icon={faMessage} className="title-icon" />
            </div>
            <div className="title-text">
              <h1>Мои чаты</h1>
              <p>Общайтесь с друзьями и коллегами</p>
            </div>
          </div>

        </div>
      </div>

      {/* Поиск и фильтры */}
      <div className="chats-controls">
        <div className="search-section">
          <div className="search-container">
            <FontAwesomeIcon icon={faSearch} className="search-icon" />
            <input
              type="text"
              placeholder="Поиск чатов и сообщений..."
              value={searchQuery}
              onChange={(e) => setSearchQuery(e.target.value)}
              className="search-input"
            />
          </div>
        </div>
        
        
      </div>

      {/* Список чатов */}
      <div className="chats-content">
        {filteredChats.length > 0 ? (
          <div className="chats-list">
            {filteredChats.map(chat => (
              <div 
                key={chat.id} 
                className="chat-card"
                onClick={() => handleChatClick(chat.id)}
              >
                <div className="chat-avatar-section">
                  <div className="chat-avatar">
                    {(() => {
                      const avatarUrl = getAvatarUrl(chat.user);
                      if (avatarUrl) {
                        return (
                          <img 
                            src={avatarUrl} 
                            alt={getUserName(chat.user)}
                            onError={(e) => {
                              e.target.style.display = 'none';
                              e.target.nextSibling.style.display = 'flex';
                            }}
                          />
                        );
                      }
                      return null;
                    })()}
                    <div className="avatar-placeholder" style={{ 
                      display: getAvatarUrl(chat.user) ? 'none' : 'flex',
                      background: `linear-gradient(135deg, #667eea 0%, #764ba2 100%)`
                    }}>
                      {getAuthorInitials(chat.user)}
                    </div>
                    {chat.isOnline && (
                      <div className="online-indicator">
                        <FontAwesomeIcon icon={faCircle} />
                      </div>
                    )}
                  </div>
                </div>
                
                <div className="chat-info">
                  <div className="chat-header">
                    <div className="chat-user-info">
                      <h3 className="chat-name">{getUserName(chat.user)}</h3>
                      {chat.user.city && (
                        <span className="chat-location">{chat.user.city}</span>
                      )}
                    </div>
                    <div className="chat-meta">
                      <span className="chat-time">
                        <FontAwesomeIcon icon={faClock} />
                        {formatTime(chat.lastMessageTime)}
                      </span>
                      <button className="chat-menu-btn">
                        <FontAwesomeIcon icon={faEllipsisV} />
                      </button>
                    </div>
                  </div>
                  
                  <div className="chat-message">
                    <p className="message-text">{chat.lastMessage}</p>
                    {chat.unreadCount > 0 && (
                      <div className="unread-badge">
                        {chat.unreadCount}
                      </div>
                    )}
                  </div>
                </div>
              </div>
            ))}
          </div>
        ) : (
          <div className="chats-empty">
            <div className="empty-container">
              <div className="empty-icon-wrapper">
                <FontAwesomeIcon icon={faUserFriends} className="empty-icon" />
              </div>
              <h3>Чатов пока нет</h3>
              <p>Начните общение с другими пользователями</p>
              <button className="btn-start-chat">
                <FontAwesomeIcon icon={faPlus} />
                Начать чат
              </button>
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default ChatsPage;
