import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { 
  faArrowLeft, 
  faSearch, 
  faMapMarkerAlt,
  faCalendar,
  faUserPlus,
  faUserMinus,
  faBell,
  faComments,
  faUser
} from '@fortawesome/free-solid-svg-icons';
import './FriendsPage.css';

const FriendsPage = () => {
  const navigate = useNavigate();
  const [searchQuery, setSearchQuery] = useState('');
  const [searchResults, setSearchResults] = useState([]);
  const [searchLoading, setSearchLoading] = useState(false);
  const [notifications, setNotifications] = useState([]);
  const [notificationsLoading, setNotificationsLoading] = useState(false);

  useEffect(() => {
    if (isLoggedIn()) {
      loadNotifications();
      // Обновляем уведомления каждые 30 секунд
      const interval = setInterval(loadNotifications, 30000);
      return () => clearInterval(interval);
    }
  }, []);

  const isLoggedIn = () => {
    return !!(localStorage.getItem('accessToken') || sessionStorage.getItem('accessToken'));
  };

  const getAuthHeaders = () => {
    const token = localStorage.getItem('accessToken') || sessionStorage.getItem('accessToken');
    return {
      'Content-Type': 'application/json',
      'Authorization': `Bearer ${token}`
    };
  };

  const searchUsers = async () => {
    if (!searchQuery.trim()) return;
    
    setSearchLoading(true);
    try {
      const response = await fetch(`http://localhost:8000/api/users/search/?q=${encodeURIComponent(searchQuery)}`, {
        headers: getAuthHeaders()
      });
      
      if (response.ok) {
        const data = await response.json();
        setSearchResults(data.users || []);
      } else {
        console.error('Ошибка поиска пользователей');
        setSearchResults([]);
      }
    } catch (error) {
      console.error('Ошибка поиска:', error);
      setSearchResults([]);
    } finally {
      setSearchLoading(false);
    }
  };

  const loadNotifications = async () => {
    if (!isLoggedIn()) return;
    
    setNotificationsLoading(true);
    try {
      const response = await fetch('http://localhost:8000/api/users/notifications/', {
        headers: getAuthHeaders()
      });
      
      if (response.ok) {
        const data = await response.json();
        setNotifications(data.notifications || []);
      } else {
        console.error('Ошибка загрузки уведомлений');
        setNotifications([]);
      }
    } catch (error) {
      console.error('Ошибка загрузки уведомлений:', error);
      setNotifications([]);
    } finally {
      setNotificationsLoading(false);
    }
  };

  const markNotificationAsRead = async (notificationId) => {
    try {
      const response = await fetch(`http://localhost:8000/api/users/notifications/${notificationId}/read/`, {
        method: 'POST',
        headers: getAuthHeaders()
      });
      
      if (response.ok) {
        // Обновляем список уведомлений
        loadNotifications();
      }
    } catch (error) {
      console.error('Ошибка отметки уведомления как прочитанного:', error);
    }
  };



  const handleFollow = async (userId) => {
    try {
      const response = await fetch(`http://localhost:8000/api/users/follow/${userId}/`, {
        method: 'POST',
        headers: getAuthHeaders()
      });
      
      if (response.ok) {
        // Обновляем поиск
        searchUsers();
      }
    } catch (error) {
      console.error('Ошибка подписки:', error);
    }
  };

  const handleUnfollow = async (userId) => {
    try {
      const response = await fetch(`http://localhost:8000/api/users/unfollow/${userId}/`, {
        method: 'POST',
        headers: getAuthHeaders()
      });
      
      if (response.ok) {
        // Обновляем поиск
        searchUsers();
      }
    } catch (error) {
      console.error('Ошибка отписки:', error);
    }
  };

  const getAuthorInitials = (authorName) => {
    if (!authorName) return 'А';
    const names = authorName.split(' ');
    return `${names[0]?.charAt(0) || 'А'}${names[1]?.charAt(0) || ''}`.toUpperCase();
  };

  const formatDate = (dateString) => {
    if (!dateString) return 'Недавно';
    const date = new Date(dateString);
    const now = new Date();
    const diffInHours = Math.floor((now - date) / (1000 * 60 * 60));
    
    if (diffInHours < 1) return 'Только что';
    if (diffInHours < 24) return `${diffInHours} ч назад`;
    
    const diffInDays = Math.floor(diffInHours / 24);
    if (diffInDays < 7) return `${diffInDays} дн назад`;
    
    return date.toLocaleDateString('ru-RU');
  };

  const getNotificationIcon = (type) => {
    switch (type) {
      case 'follow':
        return faUserPlus;
      case 'message':
        return faComments;
      default:
        return faBell;
    }
  };

  const getNotificationText = (notification) => {
    switch (notification.notification_type) {
      case 'follow':
        return `${notification.sender?.first_name || notification.sender?.username} подписался на вас`;
      case 'message':
        return `${notification.sender?.first_name || notification.sender?.username} написал вам сообщение`;
      default:
        return notification.message || 'Новое уведомление';
    }
  };

  const renderNotification = (notification) => (
    <div 
      key={notification.id} 
      className={`notification-item ${!notification.is_read ? 'unread' : ''}`}
      onClick={() => markNotificationAsRead(notification.id)}
    >
      <div className="notification-icon">
        <FontAwesomeIcon icon={getNotificationIcon(notification.notification_type)} />
      </div>
      <div className="notification-content">
        <div className="notification-text">
          {getNotificationText(notification)}
        </div>
        <div className="notification-time">
          {formatDate(notification.created_at)}
        </div>
      </div>
      {!notification.is_read && <div className="notification-dot"></div>}
    </div>
  );

  const renderUserCard = (user) => (
    <div key={user.id} className="user-card">
      <div className="user-header">
        <div className="user-avatar">
          {getAuthorInitials(user.full_name || user.username)}
        </div>
        <div className="user-info">
          <h3>{user.full_name || user.username}</h3>
          <p>
            <FontAwesomeIcon icon={faMapMarkerAlt} />
            {user.city || 'Не указан'}
          </p>
          <p>
            <FontAwesomeIcon icon={faCalendar} />
            {formatDate(user.date_joined)}
          </p>
        </div>
      </div>
      
      <div className="user-actions">
        {user.is_following ? (
          <button 
            className="unfollow-btn"
            onClick={() => handleUnfollow(user.id)}
          >
            <FontAwesomeIcon icon={faUserMinus} />
            Отписаться
          </button>
        ) : (
          <button 
            className="follow-btn"
            onClick={() => handleFollow(user.id)}
          >
            <FontAwesomeIcon icon={faUserPlus} />
            Подписаться
          </button>
        )}
      </div>
    </div>
  );

  if (!isLoggedIn()) {
    return (
      <div className="friends-page">
        <div className="friends-container">
          <div className="auth-required">
            <h2>Требуется авторизация</h2>
            <p>Для просмотра друзей и подписок необходимо войти в систему</p>
            <button className="auth-btn" onClick={() => navigate('/auth')}>
              Войти в систему
            </button>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="friends-page">
      <div className="friends-container">
        <div className="friends-header">
          <button onClick={() => navigate('/')} className="btn-back">
            <FontAwesomeIcon icon={faArrowLeft} />
            Назад
          </button>
          <h1 className="friends-title">Друзья</h1>
        </div>

        <div className="friends-content">

          {/* Секция уведомлений */}
          <div className="notifications-section">
            <h2 className="notifications-title">
              <FontAwesomeIcon icon={faBell} />
              Уведомления
            </h2>
            
            {notificationsLoading ? (
              <div className="loading">
                <div className="loading-spinner"></div>
                <p>Загрузка уведомлений...</p>
              </div>
            ) : notifications.length > 0 ? (
              <div className="notifications-list">
                {notifications.map(renderNotification)}
              </div>
            ) : (
              <div className="empty-notifications">
                <p>Уведомлений пока нет</p>
              </div>
            )}
          </div>

          <div className="search-section">
            <form className="search-form" onSubmit={(e) => { e.preventDefault(); searchUsers(); }}>
              <input
                type="text"
                className="search-input"
                placeholder="Поиск пользователей по имени или username..."
                value={searchQuery}
                onChange={(e) => setSearchQuery(e.target.value)}
              />
              <button 
                type="submit" 
                className="search-btn"
                disabled={searchLoading}
              >
                <FontAwesomeIcon icon={faSearch} />
                Найти
              </button>
            </form>

            {searchLoading ? (
              <div className="loading">
                <div className="loading-spinner"></div>
                <p>Поиск пользователей...</p>
              </div>
            ) : searchResults.length > 0 ? (
              <div className="users-grid">
                {searchResults.map(renderUserCard)}
              </div>
            ) : searchQuery && (
              <div className="empty-state">
                <div className="empty-icon">
                  <FontAwesomeIcon icon={faSearch} />
                </div>
                <h3>Пользователи не найдены</h3>
                <p>Попробуйте изменить поисковый запрос</p>
              </div>
            )}
          </div>
        </div>
      </div>
    </div>
  );
};

export default FriendsPage;
