import React, { useState, useEffect } from 'react';
import { useParams, useNavigate, Link } from 'react-router-dom';
import './ProfilePage.css';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { 
  faArrowLeft, 
  faEdit, 
  faPlus, 
  faUser, 
  faMapMarkerAlt, 
  faCalendar,
  faBaby,
  faEye,
  faEyeSlash,
  faArchive,
  faTrash,
  faPencilAlt,
  faMars,
  faVenus,
  faTimes
} from '@fortawesome/free-solid-svg-icons';
import AddChildModal from './AddChildModal';
import childrenData from '../../data/childrenData.json';

const ProfilePage = () => {
  const { userId } = useParams();
  const navigate = useNavigate();
  const [user, setUser] = useState(null);
  const [posts, setPosts] = useState([]);
  const [drafts, setDrafts] = useState([]);
  const [archived, setArchived] = useState([]);
  const [children, setChildren] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [activeTab, setActiveTab] = useState('posts');
  const [showAddChildModal, setShowAddChildModal] = useState(false);

  useEffect(() => {
    // Проверяем, чей профиль просматривается
    const currentUserData = localStorage.getItem('userData') || sessionStorage.getItem('userData');
    if (currentUserData) {
      const currentUser = JSON.parse(currentUserData);

      // Если просматривается не свой профиль, перенаправляем на UserProfilePage
      if (userId && currentUser.id != userId) {
        console.log('Просматривается чужой профиль, перенаправляем на UserProfilePage');
        navigate(`/user/${userId}`);
        return;
      }
    }

    fetchUserProfile();
    loadChildrenData();
  }, [userId, navigate]);

  // Обновляем данные при фокусе на окне (когда пользователь возвращается на страницу)
  useEffect(() => {
    const handleFocus = () => {
      fetchUserProfile();
    };

    window.addEventListener('focus', handleFocus);
    return () => window.removeEventListener('focus', handleFocus);
  }, [userId]);

  const fetchUserProfile = async () => {
    try {
      setLoading(true);
      console.log('Начинаем загрузку профиля для ID:', userId);
      console.log('Тип ID:', typeof userId);

      // Получаем токен авторизации
      const token = localStorage.getItem('accessToken') || sessionStorage.getItem('accessToken');
      const headers = token ? { 'Authorization': `Bearer ${token}` } : {};

      // Получаем данные пользователя
      const userResponse = await fetch(`http://localhost:8000/api/users/${userId}/`, {
        headers: {
          'Content-Type': 'application/json',
          ...headers
        }
      });
      console.log('Ответ от сервера для пользователя:', userResponse.status);
      console.log('Запрашиваемый ID пользователя:', userId);

      if (userResponse.ok) {
        const userData = await userResponse.json();
        console.log('Данные пользователя:', userData);
        setUser(userData.user || userData);
      } else {
        console.error('Ошибка получения пользователя:', userResponse.status);
        console.error('URL запроса:', `http://localhost:8000/api/users/${userId}/`);

        // Попробуем получить данные из localStorage/sessionStorage
        const localUserData = localStorage.getItem('userData') || sessionStorage.getItem('userData');
        if (localUserData) {
          const parsedUserData = JSON.parse(localUserData);
          console.log('Данные пользователя из localStorage:', parsedUserData);

          // Проверяем, совпадает ли ID
          if (parsedUserData.id == userId) {
            console.log('Используем данные из localStorage');
            setUser(parsedUserData);
            setError(null);
          } else {
            setError(`Пользователь не найден ID пользователя: ${userId}`);
          }
        } else {
          setError(`Пользователь не найден ID пользователя: ${userId}`);
        }
      }

      // Получаем посты пользователя
      const postsResponse = await fetch(`http://localhost:8000/api/posts/?author=${userId}`, {
        headers: {
          'Content-Type': 'application/json',
          ...headers
        }
      });
      console.log('Ответ от сервера для постов:', postsResponse.status);

      if (postsResponse.ok) {
        const postsData = await postsResponse.json();
        console.log('Данные постов:', postsData);
        const allPosts = postsData.results || postsData;

        console.log('Все посты:', allPosts);
        console.log('Статусы постов:', allPosts.map(post => ({ id: post.id, title: post.title, status: post.status, author: post.author })));

        // Фильтруем посты только текущего пользователя
        const userPosts = allPosts.filter(post => {
          const postAuthorId = post.author?.id || post.author_id;
          console.log(`Пост ${post.id}: автор ID ${postAuthorId}, текущий пользователь ID ${userId}`);
          return postAuthorId == userId;
        });

        console.log('Посты пользователя:', userPosts);

        // Разделяем посты по статусам (проверяем разные варианты статусов)
        const publishedPosts = userPosts.filter(post => 
          post.status === 'published' || post.status === 'PUBLISHED' || post.status === 1
        );
        const draftPosts = userPosts.filter(post => 
          post.status === 'draft' || post.status === 'DRAFT' || post.status === 0
        );
        const archivedPosts = userPosts.filter(post => 
          post.status === 'archived' || post.status === 'ARCHIVED' || post.status === 2
        );

        console.log('Опубликованные посты:', publishedPosts.length);
        console.log('Черновики:', draftPosts.length);
        console.log('Архивные посты:', archivedPosts.length);

        setPosts(publishedPosts);
        setDrafts(draftPosts);
        setArchived(archivedPosts);
      } else {
        console.error('Ошибка получения постов:', postsResponse.status);
        console.log('Устанавливаем пустые списки постов');
        setPosts([]);
        setDrafts([]);
        setArchived([]);
      }
          } catch (error) {
        console.error('Ошибка загрузки профиля:', error);
        setError('Ошибка загрузки профиля пользователя');
        // Устанавливаем пустые списки постов при ошибке
        setPosts([]);
        setDrafts([]);
        setArchived([]);
      } finally {
        setLoading(false);
      }
  };

  const getAuthorInitials = (authorName) => {
    if (!authorName) return 'А';
    const names = authorName.split(' ');
    return `${names[0]?.charAt(0) || 'А'}${names[1]?.charAt(0) || ''}`.toUpperCase();
  };

  const formatDate = (dateString) => {
    if (!dateString) return 'Недавно';
    const date = new Date(dateString);
    const now = new Date();
    const diffInHours = Math.floor((now - date) / (1000 * 60 * 60));

    if (diffInHours < 1) return 'Только что';
    if (diffInHours < 24) return `${diffInHours} ч назад`;

    const diffInDays = Math.floor(diffInHours / 24);
    if (diffInDays < 7) return `${diffInDays} дн назад`;

    return date.toLocaleDateString('ru-RU');
  };

  const calculateAge = (birthDate) => {
    const today = new Date();
    const birth = new Date(birthDate);
    let age = today.getFullYear() - birth.getFullYear();
    const monthDiff = today.getMonth() - birth.getMonth();

    if (monthDiff < 0 || (monthDiff === 0 && today.getDate() < birth.getDate())) {
      age--;
    }

    if (age === 0) {
      const monthAge = monthDiff < 0 ? monthDiff + 12 : monthDiff;
      return `${monthAge} мес`;
    }

    return `${age} ${getAgeWord(age)}`;
  };

  const getAgeWord = (age) => {
    if (age >= 11 && age <= 19) return 'лет';
    const lastDigit = age % 10;
    if (lastDigit === 1) return 'год';
    if (lastDigit >= 2 && lastDigit <= 4) return 'года';
    return 'лет';
  };

  const handleAddChild = (childData) => {
    const newChild = {
      id: Date.now(),
      userId: parseInt(userId),
      name: childData.name,
      gender: childData.gender,
      birthDate: childData.birthDate,
      age: calculateAge(childData.birthDate),
      avatar: null
    };
    setChildren([...children, newChild]);
  };

  const loadChildrenData = () => {
    const userChildren = childrenData.children.filter(child => child.userId === parseInt(userId));
    setChildren(userChildren);
  };

  const handleDeleteChild = (childId) => {
    setChildren(children.filter(child => child.id !== childId));
  };

  const handleDeletePost = async (postId) => {
    try {
      // Находим пост для получения slug
      const postToDelete = [...posts, ...drafts, ...archived].find(post => post.id === postId);

      if (!postToDelete) {
        console.error('Пост не найден для удаления');
        return;
      }



      // Получаем токен авторизации
      const token = localStorage.getItem('accessToken') || sessionStorage.getItem('accessToken');

      if (!token) {
        console.error('Токен не найден');
        return;
      }

      console.log(`Удаляем пост: ${postToDelete.title} (slug: ${postToDelete.slug})`);

      // Отправляем запрос на удаление
      const response = await fetch(`http://localhost:8000/api/posts/${postToDelete.slug}/delete/`, {
        method: 'DELETE',
        headers: {
          'Authorization': `Bearer ${token}`,
          'Content-Type': 'application/json',
        }
      });

      if (response.ok) {
        console.log('Пост успешно удален с сервера');
        // Удаляем пост из состояния
        setPosts(posts.filter(post => post.id !== postId));
        setDrafts(drafts.filter(post => post.id !== postId));
        setArchived(archived.filter(post => post.id !== postId));
      } else {
        console.error('Ошибка удаления поста:', response.status);
        const errorData = await response.json();
        console.error('Детали ошибки:', errorData);
        alert('Ошибка при удалении поста. Попробуйте еще раз.');
      }
    } catch (error) {
      console.error('Ошибка при удалении поста:', error);
      alert('Ошибка при удалении поста. Попробуйте еще раз.');
    }
  };

  const renderPosts = (postsList, type) => {
    if (postsList.length === 0) {
      return (
        <div className="empty-state">
          <div className="empty-icon">
            <FontAwesomeIcon icon={type === 'drafts' ? faPencilAlt : type === 'archived' ? faArchive : faUser} />
          </div>
          <p className="empty-title">
            {type === 'drafts' ? 'Черновиков пока нет' : 
             type === 'archived' ? 'Архив пуст' : 'Постов пока нет'}
          </p>
          <p className="empty-description">
            {type === 'drafts' ? 'Создайте свой первый черновик' : 
             type === 'archived' ? 'Здесь будут ваши архивные записи' : 
             'Создайте свою первую запись'}
          </p>
        </div>
      );
    }

         return (
       <div className="posts-grid">
         {postsList.map((post) => (
           <div key={post.id} className="post-item">
             {/* Заголовок */}
             <div 
               className="post-title clickable-title"
               onClick={(e) => {
                 console.log('=== КЛИК ПО ЗАГОЛОВКУ ===');
                 console.log('Заголовок:', post.title);
                 console.log('ID поста:', post.id);
                 console.log('Slug поста:', post.slug);
                 console.log('URL для перехода:', `/post-detail/${post.slug || post.id}`);
                 e.stopPropagation();
                 navigate(`/post-detail/${post.slug || post.id}`);
               }}
             >
               {console.log('Пост для отладки:', { id: post.id, title: post.title, slug: post.slug })}
               {post.title}
             </div>

             {/* Тег */}
             <div className="post-tag">
               {post.category?.name || 'Общее'}
             </div>

             {/* Информация об авторе */}
             <div className="post-author">
               <div className="author-avatar">
                 {user?.avatar ? (
                   <img 
                     src={user.avatar} 
                     alt={user.first_name}
                     onError={(e) => {
                       e.target.style.display = 'none';
                       e.target.nextSibling.style.display = 'flex';
                     }}
                   />
                 ) : null}
                 <div className="author-initials" style={{ 
                   display: user?.avatar ? 'none' : 'flex' 
                 }}>
                   {getAuthorInitials(user?.first_name || user?.username)}
                 </div>
               </div>
               <div className="author-info">
                 <span className="author-name">
                   {user?.first_name || user?.username || 'Пользователь'}
                 </span>
                 <span className="author-location">
                   <FontAwesomeIcon icon={faMapMarkerAlt} />
                   {user?.city || 'Не указан'}
                 </span>
                 <span className="post-date">
                   <FontAwesomeIcon icon={faCalendar} />
                   {formatDate(post.created_at)}
                 </span>
               </div>
             </div>

             {/* Отрывок текста статьи */}
             {post.content && (
               <div className="post-excerpt">
                 <p>{post.content.length > 67 ? `${post.content.substring(0, 67)}...` : post.content}</p>
               </div>
             )}

             {/* Кнопка "Читать дальше" */}
             <div className="post-read-more">
               <button 
                 className="read-more-btn"
                 onClick={(e) => {
                   e.stopPropagation();
                   navigate(`/post-detail/${post.slug || post.id}`);
                 }}
               >
                 Читать дальше
               </button>
             </div>

             {/* Кнопки действий */}
              <div className="post-actions">
                <button 
                  className="btn-edit"
                  onClick={(e) => {
                    e.stopPropagation();
                    navigate(`/edit-post/${post.slug}`);
                  }}
                >
                  <FontAwesomeIcon icon={faEdit} />
                  Редактировать
                </button>
               {type === 'archived' && (
                 <button className="btn-restore">
                   <FontAwesomeIcon icon={faEye} />
                   Восстановить
                 </button>
               )}
               <button 
                 className="btn-delete"
                 onClick={(e) => {
                   e.stopPropagation();
                   handleDeletePost(post.id);
                 }}
               >
                 <FontAwesomeIcon icon={faTrash} />
                 Удалить
               </button>
             </div>
           </div>
         ))}
       </div>
     );
  };

  if (loading) {
    return (
      <div className="profile-loading">
        <div className="loading-spinner"></div>
        <p>Загрузка профиля...</p>
      </div>
    );
  }

  if (error || !user) {
    return (
      <div className="profile-error">
        <h2>Ошибка</h2>
        <p>{error || 'Пользователь не найден'}</p>
        <p>ID пользователя: {userId}</p>
        <button onClick={() => navigate('/')} className="btn-back">
          <FontAwesomeIcon icon={faArrowLeft} />
          Вернуться на главную
        </button>
      </div>
    );
  }

  return (
    <div className="profile-page">
      <div className="profile-container">
        {/* Кнопка назад */}
        <div className="profile-header">
          <button onClick={() => navigate('/')} className="btn-back">
            <FontAwesomeIcon icon={faArrowLeft} />
            Назад
          </button>
        </div>

        {/* Информация о пользователе */}
        <div className="user-profile-section">
          <div className="user-avatar">
            {user?.avatar ? (
              <img src={user.avatar} alt={user.first_name} />
            ) : (
              <div className="avatar-placeholder">
                {getAuthorInitials(user?.first_name || user?.username)}
              </div>
            )}
          </div>
          <div className="user-info">
            <h1 className="user-name">{user?.first_name || user?.username || 'Пользователь'}</h1>
            <div className="user-details">
              <span className="user-location">
                <FontAwesomeIcon icon={faMapMarkerAlt} />
                {user?.city || 'Не указан'}
              </span>
              <span className="user-join-date">
                <FontAwesomeIcon icon={faCalendar} />
                {user?.date_joined ? formatDate(user.date_joined) : 'Недавно'}
              </span>
            </div>
            <div className="user-username">@{user?.username}</div>
          </div>
                     <div className="user-actions">
             <button 
               className="btn-edit-profile"
               onClick={() => navigate('/edit-profile')}
             >
               <FontAwesomeIcon icon={faEdit} />
               Редактировать профиль
             </button>
             <button 
               className="btn-add-child"
               onClick={() => setShowAddChildModal(true)}
             >
               <FontAwesomeIcon icon={faBaby} />
               Добавить ребенка
             </button>
           </div>
        </div>

                 {/* Дети пользователя */}
         {children.length > 0 && (
           <div className="user-children-section">
             <h3 className="children-title">
               <FontAwesomeIcon icon={faBaby} />
               Мои дети
             </h3>
             <div className="children-grid">
                               {children.map((child) => (
                  <div key={child.id} className="child-card">
                    <div className="child-avatar">
                      <FontAwesomeIcon 
                        icon={child.gender === 'male' ? faMars : faVenus} 
                        className={`gender-icon ${child.gender}`}
                      />
                    </div>
                    <div className="child-info">
                      <div className="child-name">{child.name}</div>
                      <div className="child-age">{child.age}</div>
                    </div>
                    <button 
                      className="child-delete-btn"
                      onClick={() => handleDeleteChild(child.id)}
                      title="Удалить ребенка"
                    >
                      <FontAwesomeIcon icon={faTimes} />
                    </button>
                  </div>
                ))}
             </div>
           </div>
         )}

         {/* Статистика */}
         <div className="user-stats">
           <div className="stat-item">
             <div className="stat-number">{posts.length}</div>
             <div className="stat-label">постов</div>
           </div>
           <div className="stat-item">
             <div className="stat-number">{children.length}</div>
             <div className="stat-label">детей</div>
           </div>
           <div className="stat-item">
             <div className="stat-number">0</div>
             <div className="stat-label">подписчиков</div>
           </div>
           <div className="stat-item">
             <div className="stat-number">0</div>
             <div className="stat-label">подписок</div>
           </div>
         </div>

        {/* Вкладки */}
        <div className="profile-tabs">
          <button 
            className={`tab ${activeTab === 'posts' ? 'active' : ''}`}
            onClick={() => setActiveTab('posts')}
          >
            <FontAwesomeIcon icon={faUser} />
            Записи ({posts.length})
          </button>
          <button 
            className={`tab ${activeTab === 'drafts' ? 'active' : ''}`}
            onClick={() => setActiveTab('drafts')}
          >
            <FontAwesomeIcon icon={faPencilAlt} />
            Черновики ({drafts.length})
          </button>

          <button 
            className={`tab ${activeTab === 'archive' ? 'active' : ''}`}
            onClick={() => setActiveTab('archive')}
          >
            <FontAwesomeIcon icon={faArchive} />
            Архив ({archived.length})
          </button>
        </div>

        {/* Контент вкладок */}
        <div className="tab-content">
          {activeTab === 'posts' && renderPosts(posts, 'posts')}
          {activeTab === 'drafts' && renderPosts(drafts, 'drafts')}

          {activeTab === 'archive' && renderPosts(archived, 'archived')}
                 </div>
       </div>

       {/* Модальное окно добавления ребенка */}
       <AddChildModal
         isOpen={showAddChildModal}
         onClose={() => setShowAddChildModal(false)}
         onAddChild={handleAddChild}
       />
     </div>
   );
 };

export default ProfilePage;
