import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faEye, faComment, faCalendar, faMapMarkerAlt, faPen } from '@fortawesome/free-solid-svg-icons';
import './BabyBlogPosts.css';

const BabyBlogPosts = ({ limit = 4 }) => {
  const [posts, setPosts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    fetchPosts();
  }, []);

  const fetchPosts = async () => {
    try {
      setLoading(true);
      let url = 'http://localhost:8000/api/posts/';
      
      if (limit) {
        url += `?page_size=${limit}`;
      }
      
      const response = await fetch(url);
      if (response.ok) {
        const data = await response.json();
        const postsData = data.results || data;
        
        // Преобразуем данные в формат BabyBlog
        const transformedPosts = postsData.map((post, index) => ({
          id: post.id,
          category: post.category?.name?.toUpperCase() || 'ОБЩЕЕ',
          title: post.title.length > 50 ? `${post.title.substring(0, 50)}...` : post.title,
          tag: post.category?.name || 'Общее',
          author: {
            name: post.author?.first_name && post.author?.last_name 
              ? `${post.author.first_name} ${post.author.last_name}`
              : post.author?.username || 'Автор',
            avatar: post.author?.avatar || null,
            location: post.author?.city || 'Москва',
            isOnline: Math.random() > 0.5 // Случайный статус онлайн
          },
          date: formatDate(post.published_at || post.created_at),
          views: post.views_count || Math.floor(Math.random() * 20) + 1,
          comments: post.comments_count || Math.floor(Math.random() * 5),
          slug: post.slug
        }));
        
        setPosts(transformedPosts);
      } else {
        setError('Ошибка загрузки постов');
      }
    } catch (error) {
      console.error('Ошибка загрузки постов:', error);
      setError('Ошибка загрузки постов');
    } finally {
      setLoading(false);
    }
  };

  const formatDate = (dateString) => {
    try {
      const date = new Date(dateString);
      if (isNaN(date.getTime())) {
        return 'Недавно';
      }
      
      const now = new Date();
      const diffInHours = Math.floor((now - date) / (1000 * 60 * 60));
      
      if (diffInHours < 1) {
        return 'Недавно';
      } else if (diffInHours < 24) {
        return `${diffInHours} час${diffInHours === 1 ? '' : diffInHours < 5 ? 'а' : 'ов'} назад`;
      } else {
        const diffInDays = Math.floor(diffInHours / 24);
        return `${diffInDays} дн${diffInDays === 1 ? 'ь' : diffInDays < 5 ? 'я' : 'ей'} назад`;
      }
    } catch (error) {
      return 'Недавно';
    }
  };

  const getAuthorInitials = (authorName) => {
    if (!authorName) return 'А';
    const names = authorName.split(' ');
    return `${names[0]?.charAt(0) || 'А'}${names[1]?.charAt(0) || ''}`.toUpperCase();
  };

  if (loading) {
    return (
      <div className="babyblog-posts-loading">
        <div className="loading-spinner"></div>
        <p>Загрузка постов...</p>
      </div>
    );
  }

  if (error) {
    return (
      <div className="babyblog-posts-error">
        <p>{error}</p>
        <button onClick={fetchPosts} className="btn-retry">
          Попробовать снова
        </button>
      </div>
    );
  }

  if (posts.length === 0) {
    return (
      <div className="babyblog-posts-empty">
        <div className="empty-icon">
          <FontAwesomeIcon icon={faEye} />
        </div>
        <h3>Постов пока нет</h3>
        <p>Будьте первым, кто поделится своими мыслями и опытом!</p>
        <Link to="/create-post" className="btn-create-post">
          <FontAwesomeIcon icon={faPen} />
          Написать первый пост
        </Link>
      </div>
    );
  }

  return (
    <div className="babyblog-posts-container">
      <div className="babyblog-posts-grid">
        {posts.map(post => (
          <article key={post.id} className="babyblog-post-card">
            {/* Верхняя секция с градиентом и иконкой */}
            <div className="post-top-section">
              <div className="post-top-gradient">
                <FontAwesomeIcon icon={faEye} className="post-top-icon" />
              </div>
            </div>
            
            {/* Содержимое поста */}
            <div className="post-content">
              {/* Категория */}
              <div className="post-category">
                {post.category}
              </div>
              
              {/* Заголовок */}
              <h3 className="post-title">
                <Link to={`/post-detail/${post.slug}`}>
                  {post.title}
                </Link>
              </h3>
              
              {/* Тег */}
              <div className="post-tag">
                {post.tag}
              </div>
              
              {/* Информация об авторе */}
              <div className="post-author">
                <div className="author-avatar">
                  {post.author.avatar ? (
                    <img 
                      src={post.author.avatar} 
                      alt={post.author.name}
                      onError={(e) => {
                        e.target.style.display = 'none';
                        e.target.nextSibling.style.display = 'flex';
                      }}
                    />
                  ) : null}
                  <div className="author-initials" style={{ 
                    display: post.author.avatar ? 'none' : 'flex' 
                  }}>
                    {getAuthorInitials(post.author.name)}
                  </div>
                  {post.author.isOnline && <div className="online-indicator"></div>}
                </div>
                <div className="author-info">
                  <span className="author-name">{post.author.name}</span>
                  <span className="author-location">
                    <FontAwesomeIcon icon={faMapMarkerAlt} />
                    {post.author.location}
                  </span>
                  <span className="post-date">
                    <FontAwesomeIcon icon={faCalendar} />
                    {post.date}
                  </span>
                </div>
              </div>
              
              {/* Статистика */}
              <div className="post-stats">
                <span className="post-stat">
                  <FontAwesomeIcon icon={faEye} />
                  {post.views}
                </span>
                <span className="post-stat">
                  <FontAwesomeIcon icon={faComment} />
                  {post.comments}
                </span>
              </div>
              
              {/* Кнопка "Читать дальше" */}
              <div className="post-actions">
                <Link to={`/post-detail/${post.slug}`} className="read-more-btn">
                  Читать дальше
                </Link>
              </div>
            </div>
          </article>
        ))}
      </div>
    </div>
  );
};

export default BabyBlogPosts;
