import React from 'react';
import { useNavigate } from 'react-router-dom';
import './FeaturedCards.css';

const FeaturedCards = () => {
  const navigate = useNavigate();

  const featuredCards = [
    {
      id: 1,
      title: 'Секреты спокойного сна: как уложить малыша без слез',
      color: 'purple',
      illustration: 'sleeping-baby',
      postId: 'sleeping-baby'
    },
    {
      id: 2,
      title: 'Первый год жизни: важные этапы развития ребенка',
      color: 'pink',
      illustration: 'baby-development',
      postId: 'baby-development'
    }
  ];

  const handleCardClick = (postId) => {
    navigate(`/post/${postId}`);
  };

  return (
    <div className="featured-content">
      {featuredCards.map(card => (
        <div 
          key={card.id} 
          className={`featured-card ${card.color}`}
          onClick={() => handleCardClick(card.postId)}
        >
          <div className="card-content">
            <h3>{card.title}</h3>
          </div>
          <div className="card-image">
            <div className={`illustration ${card.illustration}`}></div>
          </div>
        </div>
      ))}
    </div>
  );
};

export default FeaturedCards;
