import React, { useState, useEffect, useRef } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { 
  faArrowLeft, 
  faPaperPlane, 
  faImage
} from '@fortawesome/free-solid-svg-icons';
import chatData from '../../data/simpleChat.json';
import './SimpleChat.css';

const SimpleChat = () => {
  const { userId } = useParams();
  const navigate = useNavigate();
  const messagesEndRef = useRef(null);
  const fileInputRef = useRef(null);
  
  const [messages, setMessages] = useState([]);
  const [newMessage, setNewMessage] = useState('');
  const [user, setUser] = useState(null);
  const [isLoading, setIsLoading] = useState(true);

  useEffect(() => {
    loadChat();
  }, [userId]);

  useEffect(() => {
    scrollToBottom();
  }, [messages]);

  const scrollToBottom = () => {
    if (messagesEndRef.current) {
      messagesEndRef.current.scrollIntoView({ behavior: 'smooth' });
    }
  };

  const loadChat = async () => {
    try {
      setIsLoading(true);
      
      // Загружаем данные пользователя
      const token = localStorage.getItem('accessToken') || sessionStorage.getItem('accessToken');
      if (!token) {
        navigate('/login');
        return;
      }

      const response = await fetch(`http://localhost:8000/api/users/${userId}/`, {
        headers: {
          'Authorization': `Bearer ${token}`,
          'Content-Type': 'application/json'
        }
      });

      if (response.ok) {
        const responseData = await response.json();
        console.log('Данные пользователя:', responseData);
        
        if (responseData.success && responseData.user) {
          const userData = responseData.user;
          
          // Формируем имя пользователя
          let userName = 'Пользователь';
          if (userData.first_name && userData.last_name) {
            userName = `${userData.first_name} ${userData.last_name}`.trim();
          } else if (userData.first_name) {
            userName = userData.first_name;
          } else if (userData.username) {
            userName = userData.username;
          }

          setUser({
            id: userData.id,
            name: userName,
            avatar: userData.avatar,
            lastSeen: '3 м назад',
            isOnline: false
          });
        } else {
          console.error('Ошибка получения данных пользователя:', responseData.message);
          navigate('/friends');
          return;
        }
      } else {
        console.error('Пользователь не найден');
        navigate('/friends');
        return;
      }

      // Загружаем сообщения чата
      await loadChatMessages();
      
    } catch (error) {
      console.error('Ошибка загрузки чата:', error);
    } finally {
      setIsLoading(false);
    }
  };

  const loadChatMessages = async () => {
    try {
      const token = localStorage.getItem('accessToken') || sessionStorage.getItem('accessToken');
      if (!token) return;

      // Ищем существующий чат
      const chatsResponse = await fetch('http://localhost:8000/api/users/chats/', {
        headers: {
          'Authorization': `Bearer ${token}`,
          'Content-Type': 'application/json'
        }
      });

      if (chatsResponse.ok) {
        const chatsData = await chatsResponse.json();
        if (chatsData.success && chatsData.chats) {
          const existingChat = chatsData.chats.find(chat => 
            chat.other_participant && chat.other_participant.id === parseInt(userId)
          );

          if (existingChat) {
            // Загружаем сообщения существующего чата
            const messagesResponse = await fetch(`http://localhost:8000/api/users/chats/${existingChat.id}/`, {
              headers: {
                'Authorization': `Bearer ${token}`,
                'Content-Type': 'application/json'
              }
            });

            if (messagesResponse.ok) {
              const messagesData = await messagesResponse.json();
              if (messagesData.success && messagesData.messages) {
                const formattedMessages = messagesData.messages.map(msg => ({
                  id: msg.id,
                  text: msg.content,
                  timestamp: msg.created_at,
                  isOwn: msg.sender_name === 'Вы',
                  messageType: msg.message_type,
                  fileUrl: msg.file_url
                }));
                setMessages(formattedMessages);
              }
            }
          }
        }
      }
    } catch (error) {
      console.error('Ошибка загрузки сообщений:', error);
    }
  };

  const handleSendMessage = async () => {
    if (!newMessage.trim()) return;

    try {
      const token = localStorage.getItem('accessToken') || sessionStorage.getItem('accessToken');
      if (!token) return;

      // Ищем или создаем чат
      let chatId = null;
      
      const chatsResponse = await fetch('http://localhost:8000/api/users/chats/', {
        headers: {
          'Authorization': `Bearer ${token}`,
          'Content-Type': 'application/json'
        }
      });

      if (chatsResponse.ok) {
        const chatsData = await chatsResponse.json();
        if (chatsData.success && chatsData.chats) {
          const existingChat = chatsData.chats.find(chat => 
            chat.other_participant && chat.other_participant.id === parseInt(userId)
          );
          
          if (existingChat) {
            chatId = existingChat.id;
          }
        }
      }

      // Если чат не найден, создаем новый
      if (!chatId) {
        const createResponse = await fetch('http://localhost:8000/api/users/chats/create/', {
          method: 'POST',
          headers: {
            'Authorization': `Bearer ${token}`,
            'Content-Type': 'application/json'
          },
          body: JSON.stringify({ participant_id: parseInt(userId) })
        });

        if (createResponse.ok) {
          const createData = await createResponse.json();
          if (createData.success && createData.chat) {
            chatId = createData.chat.id;
          }
        }
      }

      if (!chatId) {
        alert('Не удалось создать или найти чат');
        return;
      }

      // Отправляем сообщение
      const messageResponse = await fetch(`http://localhost:8000/api/users/chats/${chatId}/messages/`, {
        method: 'POST',
        headers: {
          'Authorization': `Bearer ${token}`,
          'Content-Type': 'application/json'
        },
        body: JSON.stringify({
          content: newMessage.trim(),
          message_type: 'text'
        })
      });

      if (messageResponse.ok) {
        const messageData = await messageResponse.json();
        if (messageData.success) {
          // Добавляем новое сообщение в список
          const newMsg = {
            id: messageData.message.id,
            text: messageData.message.content,
            timestamp: messageData.message.created_at,
            isOwn: true,
            messageType: messageData.message.message_type
          };
          
          setMessages(prev => [...prev, newMsg]);
        }
      }
      
      // Очищаем поле ввода
      setNewMessage('');
      
    } catch (error) {
      console.error('Ошибка отправки сообщения:', error);
    }
  };

  const handleKeyPress = (e) => {
    if (e.key === 'Enter' && !e.shiftKey) {
      e.preventDefault();
      handleSendMessage();
    }
  };

  const handleFileSelect = (e) => {
    const file = e.target.files[0];
    if (file) {
      // Здесь можно добавить логику отправки файла
      console.log('Выбран файл:', file.name);
    }
  };

  const formatTime = (timestamp) => {
    const date = new Date(timestamp);
    return date.toLocaleTimeString('ru-RU', { 
      hour: '2-digit', 
      minute: '2-digit' 
    });
  };

  if (isLoading) {
    return (
      <div className="simple-chat">
        <div className="chat-loading">
          <div className="loading-spinner"></div>
          <p>Загрузка чата...</p>
        </div>
      </div>
    );
  }

  if (!user) {
    return (
      <div className="simple-chat">
        <div className="chat-error">
          <p>Пользователь не найден</p>
          <button onClick={() => navigate('/friends')}>Вернуться к друзьям</button>
        </div>
      </div>
    );
  }

  return (
    <div className="simple-chat">
      {/* Заголовок чата */}
      <div className="chat-header">
        <button className="back-button" onClick={() => navigate('/chats')}>
          <FontAwesomeIcon icon={faArrowLeft} />
          <span>Переписки</span>
        </button>
        
        <div className="chat-user-info">
          <h3 className="user-name">{user.name || 'Загрузка...'}</h3>
        </div>
      </div>

      {/* Контейнер сообщений */}
      <div className="messages-container">
        {messages.length === 0 ? (
          <div className="empty-messages">
            <p className="empty-title">{chatData.emptyState.title}</p>
            <p className="empty-subtitle">{chatData.emptyState.subtitle}</p>
          </div>
        ) : (
          <div className="messages-list">
            {messages.map((message) => (
              <div 
                key={message.id} 
                className={`message ${message.isOwn ? 'my-message' : 'incoming-message'}`}
              >
                <div className="message-content">
                  <p className="message-text">{message.text}</p>
                  <span className="message-time">{formatTime(message.timestamp)}</span>
                </div>
              </div>
            ))}
            <div ref={messagesEndRef} />
          </div>
        )}
      </div>

      {/* Поле ввода сообщения */}
      <div className="message-input-container">
        <div className="message-input-wrapper">
          <input
            type="file"
            ref={fileInputRef}
            onChange={handleFileSelect}
            style={{ display: 'none' }}
            accept="image/*"
          />
          
          <button 
            className="attach-button"
            onClick={() => fileInputRef.current?.click()}
          >
            <FontAwesomeIcon icon={faImage} />
          </button>
          
          <input
            type="text"
            className="message-input"
            placeholder="Ваше сообщение"
            value={newMessage}
            onChange={(e) => setNewMessage(e.target.value)}
            onKeyPress={handleKeyPress}
          />
          
          <button 
            className="send-button"
            onClick={handleSendMessage}
            disabled={!newMessage.trim()}
          >
            <FontAwesomeIcon icon={faPaperPlane} />
            <span>Отправить</span>
          </button>
        </div>
      </div>
    </div>
  );
};

export default SimpleChat;
