import React, { useState, useEffect, useRef } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { 
  faArrowLeft, 
  faPaperPlane, 
  faEdit, 
  faTrash, 
  faCheck, 
  faTimes,
  faUser,
  faMessage
} from '@fortawesome/free-solid-svg-icons';
import { getChat, saveMessage, updateMessage, deleteMessage } from '../../utils/chatStorage';
import { getUserInfo } from '../../utils/userUtils';
import './ChatPage.css';

const ChatPage = () => {
  const { userId } = useParams();
  const navigate = useNavigate();
  const messagesEndRef = useRef(null);
  const [messages, setMessages] = useState([]);
  const [newMessage, setNewMessage] = useState('');
  const [editingMessageId, setEditingMessageId] = useState(null);
  const [editText, setEditText] = useState('');
  const [user, setUser] = useState(null);
  const [isLoading, setIsLoading] = useState(true);
  const [isTyping, setIsTyping] = useState(false);
  const [typingTimeout, setTypingTimeout] = useState(null);

  // Проверяем userId сразу при загрузке
  
  if (!userId) {
    console.error('userId не найден в параметрах');
    return (
      <div className="chat-loading">
        <p>Ошибка: ID пользователя не найден</p>
        <button onClick={() => navigate(-1)}>Вернуться назад</button>
      </div>
    );
  }

  // Загружаем пользователя и сообщения
  useEffect(() => {
    const loadChat = async () => {
      try {
        // Загружаем существующие сообщения
        const existingMessages = getChat(userId);
        setMessages(existingMessages);

        // Получаем информацию о пользователе
        // Сначала проверяем localStorage на наличие реальных данных
        const userInfo = await getUserInfo(userId);
        
        if (userInfo && userInfo.name && userInfo.name !== `Пользователь ${userId}`) {
          setUser(userInfo);
        } else {
          const mockUserInfo = {
            id: userId,
            name: `Пользователь ${userId}`,
            avatar: '',
            city: 'Неизвестный город',
            lastSeen: new Date().toISOString()
          };
          setUser(mockUserInfo);
        }
        
        setIsLoading(false);
      } catch (error) {
        // Fallback на моковые данные
        const fallbackUser = {
          id: userId,
          name: `Пользователь ${userId}`,
          avatar: '',
          city: 'Неизвестный город',
          lastSeen: new Date().toISOString()
        };
        setUser(fallbackUser);
        setIsLoading(false);
      }
    };

    loadChat();
  }, [userId]);

  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  };

  useEffect(() => {
    scrollToBottom();
  }, [messages]);

  const handleSendMessage = () => {
    if (newMessage.trim()) {
      const messageData = {
        text: newMessage.trim(),
        sender: 'user'
      };

      // Сохраняем сообщение в localStorage
      const savedMessage = saveMessage(userId, messageData);
      
      if (savedMessage) {
        // Добавляем класс для анимации нового сообщения
        const messageWithAnimation = { ...savedMessage, isNew: true };
        setMessages(prev => [...prev, messageWithAnimation]);
        setNewMessage('');
        
        // Убираем анимацию через некоторое время
        setTimeout(() => {
          setMessages(prev => prev.map(msg => 
            msg.id === savedMessage.id ? { ...msg, isNew: false } : msg
          ));
        }, 500);
      }
      
      // Останавливаем индикатор печати
      setIsTyping(false);
      if (typingTimeout) {
        clearTimeout(typingTimeout);
        setTypingTimeout(null);
      }
    }
  };

  const handleKeyPress = (e) => {
    if (e.key === 'Enter' && !e.shiftKey) {
      e.preventDefault();
      handleSendMessage();
    }
  };

  const handleInputChange = (e) => {
    setNewMessage(e.target.value);
    
    // Показываем индикатор печати
    if (!isTyping) {
      setIsTyping(true);
    }
    
    // Очищаем предыдущий таймаут
    if (typingTimeout) {
      clearTimeout(typingTimeout);
    }
    
    // Устанавливаем новый таймаут для скрытия индикатора печати
    const timeout = setTimeout(() => {
      setIsTyping(false);
    }, 1000);
    
    setTypingTimeout(timeout);
  };

  const handleEditMessage = (messageId) => {
    const message = messages.find(msg => msg.id === messageId);
    if (message) {
      setEditingMessageId(messageId);
      setEditText(message.text);
    }
  };

  const handleSaveEdit = () => {
    if (editText.trim() && editingMessageId) {
      // Обновляем сообщение в localStorage
      const success = updateMessage(userId, editingMessageId, editText.trim());
      
      if (success) {
        setMessages(prev => prev.map(msg => 
          msg.id === editingMessageId 
            ? { ...msg, text: editText.trim(), isEdited: true, editedAt: new Date().toISOString() }
            : msg
        ));
        setEditingMessageId(null);
        setEditText('');
      }
    }
  };

  const handleCancelEdit = () => {
    setEditingMessageId(null);
    setEditText('');
  };

  const handleDeleteMessage = (messageId) => {
    if (window.confirm('Вы уверены, что хотите удалить это сообщение?')) {
      // Удаляем сообщение из localStorage
      const success = deleteMessage(userId, messageId);
      
      if (success) {
        setMessages(prev => prev.filter(msg => msg.id !== messageId));
      }
    }
  };

  const formatTime = (timestamp) => {
    const date = new Date(timestamp);
    return date.toLocaleTimeString('ru-RU', { 
      hour: '2-digit', 
      minute: '2-digit' 
    });
  };

  const formatDate = (timestamp) => {
    const date = new Date(timestamp);
    const today = new Date();
    const yesterday = new Date(today);
    yesterday.setDate(yesterday.getDate() - 1);

    if (date.toDateString() === today.toDateString()) {
      return 'Сегодня';
    } else if (date.toDateString() === yesterday.toDateString()) {
      return 'Вчера';
    } else {
      return date.toLocaleDateString('ru-RU');
    }
  };

  if (isLoading) {
    return (
      <div className="chat-loading">
        <div className="loading-spinner"></div>
        <p>Загрузка чата...</p>
      </div>
    );
  }

  // Проверяем, что у нас есть информация о пользователе
  if (!user) {
    // Fallback на моковые данные прямо в рендере
    const fallbackUser = {
      id: userId,
      name: `Пользователь ${userId}`,
      avatar: '',
      city: 'Неизвестный город',
      lastSeen: new Date().toISOString()
    };
    return (
      <div className="chat-loading">
        <div className="loading-spinner"></div>
        <p>Загрузка информации о пользователе...</p>
        <button 
          onClick={() => setUser(fallbackUser)}
          style={{ 
            marginTop: '20px', 
            padding: '10px 20px', 
            background: '#667eea', 
            color: 'white', 
            border: 'none', 
            borderRadius: '8px',
            cursor: 'pointer'
          }}
        >
          Принудительно загрузить данные
        </button>
      </div>
    );
  }

  return (
    <div className="chat-page">
      {/* Заголовок чата */}
      <div className="chat-header">
        <button className="back-button" onClick={() => navigate(-1)}>
          <FontAwesomeIcon icon={faArrowLeft} />
        </button>
        
        <div className="chat-user-info">
          <div className="user-avatar">
            {user?.avatar ? (
              <img src={user.avatar} alt={user.name} />
            ) : (
              <div className="avatar-placeholder">
                <FontAwesomeIcon icon={faUser} />
              </div>
            )}
          </div>
          
          <div className="user-details">
            <h3 className="user-name">
              {user?.name || 'Загрузка...'}
            </h3>
            {user?.city && (
              <span className="user-city">
                📍 {user.city}
              </span>
            )}
          </div>
        </div>
      </div>

      {/* Контейнер сообщений */}
      <div className="messages-container">
        {messages.length === 0 ? (
          <div className="empty-chat">
            <div className="empty-chat-icon">
              <FontAwesomeIcon icon={faMessage} />
            </div>
            <h3>Чат пуст</h3>
            <p>Начните разговор, отправив первое сообщение</p>
          </div>
        ) : (
          <div className="messages-list">
            {messages.map((message) => (
              <div key={message.id} className={`message ${message.sender === 'user' ? 'user-message' : 'other-message'}`}>
                {editingMessageId === message.id ? (
                  <div className="edit-message-form">
                    <textarea
                      className="edit-textarea"
                      value={editText}
                      onChange={(e) => setEditText(e.target.value)}
                      placeholder="Редактировать сообщение..."
                      autoFocus
                    />
                    <div className="edit-actions">
                      <button 
                        className="edit-btn save-btn" 
                        onClick={handleSaveEdit}
                      >
                        <FontAwesomeIcon icon={faCheck} />
                      </button>
                      <button 
                        className="edit-btn cancel-btn" 
                        onClick={handleCancelEdit}
                      >
                        <FontAwesomeIcon icon={faTimes} />
                      </button>
                    </div>
                  </div>
                ) : (
                  <>
                    <div className="message-content">
                      <p className="message-text">{message.text}</p>
                      {message.isEdited && (
                        <span className="edited-indicator">(изменено)</span>
                      )}
                      <div className="message-meta">
                        <span className="message-time">{formatTime(message.timestamp)}</span>
                      </div>
                    </div>
                    
                    {/* Кнопки действий для сообщений пользователя */}
                    {message.sender === 'user' && (
                      <div className="message-actions">
                        <button 
                          className="action-btn edit-btn" 
                          onClick={() => handleEditMessage(message.id)}
                          title="Редактировать"
                        >
                          <FontAwesomeIcon icon={faEdit} />
                        </button>
                        <button 
                          className="action-btn delete-btn" 
                          onClick={() => handleDeleteMessage(message.id)}
                          title="Удалить"
                        >
                          <FontAwesomeIcon icon={faTrash} />
                        </button>
                      </div>
                    )}
                  </>
                )}
              </div>
            ))}
            <div ref={messagesEndRef} />
          </div>
        )}
      </div>

      {/* Форма ввода сообщения */}
      <div className="message-input-container">
        <div className="message-input-wrapper">
          <textarea
            className="message-input"
            value={newMessage}
            onChange={handleInputChange}
            onKeyPress={handleKeyPress}
            placeholder="Введите сообщение..."
            rows="1"
          />
          <button 
            className="send-button" 
            onClick={handleSendMessage}
            disabled={!newMessage.trim()}
          >
            <FontAwesomeIcon icon={faPaperPlane} />
          </button>
        </div>
      </div>
    </div>
  );
};

export default ChatPage;
