import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import './Notifications.css';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { 
  faBell, 
  faUserPlus, 
  faComments, 
  faTimes,
  faArrowLeft,
  faMapMarkerAlt,
  faCalendar
} from '@fortawesome/free-solid-svg-icons';

const Notifications = () => {
  const navigate = useNavigate();
  const [notifications, setNotifications] = useState([]);
  const [loading, setLoading] = useState(true);
  const [unreadCount, setUnreadCount] = useState(0);

  useEffect(() => {
    loadNotifications();
    // Обновляем уведомления каждые 30 секунд
    const interval = setInterval(loadNotifications, 30000);
    return () => clearInterval(interval);
  }, []);

  const isLoggedIn = () => {
    return !!(localStorage.getItem('accessToken') || sessionStorage.getItem('accessToken'));
  };

  const getAuthHeaders = () => {
    const token = localStorage.getItem('accessToken') || sessionStorage.getItem('accessToken');
    return token ? { 'Authorization': `Bearer ${token}` } : {};
  };

  const loadNotifications = async () => {
    if (!isLoggedIn()) return;

    try {
      const response = await fetch('http://localhost:8000/api/users/notifications/', {
        headers: {
          'Content-Type': 'application/json',
          ...getAuthHeaders()
        }
      });

      if (response.ok) {
        const data = await response.json();
        setNotifications(data.notifications || []);
        setUnreadCount(data.unread_count || 0);
      } else {
        console.error('Ошибка загрузки уведомлений:', response.status);
      }
    } catch (error) {
      console.error('Ошибка загрузки уведомлений:', error);
    } finally {
      setLoading(false);
    }
  };

  const markAsRead = async (notificationId) => {
    try {
      const response = await fetch(`http://localhost:8000/api/users/notifications/${notificationId}/read/`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          ...getAuthHeaders()
        }
      });

      if (response.ok) {
        // Обновляем локальное состояние
        setNotifications(prev => 
          prev.map(notif => 
            notif.id === notificationId 
              ? { ...notif, is_read: true }
              : notif
          )
        );
        setUnreadCount(prev => Math.max(0, prev - 1));
      }
    } catch (error) {
      console.error('Ошибка отметки уведомления:', error);
    }
  };

  const markAllAsRead = async () => {
    try {
      const response = await fetch('http://localhost:8000/api/users/notifications/mark-all-read/', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          ...getAuthHeaders()
        }
      });

      if (response.ok) {
        setNotifications(prev => prev.map(notif => ({ ...notif, is_read: true })));
        setUnreadCount(0);
      }
    } catch (error) {
      console.error('Ошибка отметки всех уведомлений:', error);
    }
  };

  const getAuthorInitials = (authorName) => {
    if (!authorName) return 'А';
    const names = authorName.split(' ');
    return `${names[0]?.charAt(0) || 'А'}${names[1]?.charAt(0) || ''}`.toUpperCase();
  };

  const formatDate = (dateString) => {
    if (!dateString) return 'Только что';
    const date = new Date(dateString);
    const now = new Date();
    const diffInMinutes = Math.floor((now - date) / (1000 * 60));
    
    if (diffInMinutes < 1) return 'Только что';
    if (diffInMinutes < 60) return `${diffInMinutes} мин назад`;
    
    const diffInHours = Math.floor(diffInMinutes / 60);
    if (diffInHours < 24) return `${diffInHours} ч назад`;
    
    const diffInDays = Math.floor(diffInHours / 24);
    if (diffInDays < 7) return `${diffInDays} дн назад`;
    
    return date.toLocaleDateString('ru-RU');
  };

  const getNotificationIcon = (type) => {
    switch (type) {
      case 'follow':
        return faUserPlus;
      case 'message':
        return faComments;
      default:
        return faBell;
    }
  };

  const getNotificationText = (notification) => {
    switch (notification.type) {
      case 'follow':
        return `@${notification.sender.username} подписался на вас`;
      case 'message':
        return `@${notification.sender.username} написал вам сообщение`;
      default:
        return notification.message || 'Новое уведомление';
    }
  };

  const handleNotificationClick = (notification) => {
    // Отмечаем как прочитанное
    if (!notification.is_read) {
      markAsRead(notification.id);
    }

    // Переходим к соответствующей странице
    switch (notification.type) {
      case 'follow':
        navigate(`/user/${notification.sender.id}`);
        break;
      case 'message':
        // Здесь будет переход к чату
        navigate(`/chat/${notification.sender.id}`);
        break;
      default:
        break;
    }
  };

  if (!isLoggedIn()) {
    return (
      <div className="notifications-page">
        <div className="notifications-container">
          <div className="auth-required">
            <h2>Требуется авторизация</h2>
            <p>Для просмотра уведомлений необходимо войти в систему</p>
            <button onClick={() => navigate('/auth')} className="auth-btn">
              Войти
            </button>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="notifications-page">
      <div className="notifications-container">
        {/* Заголовок */}
        <div className="notifications-header">
          <button onClick={() => navigate('/')} className="btn-back">
            <FontAwesomeIcon icon={faArrowLeft} />
            Назад
          </button>
          <h1>Уведомления</h1>
          {unreadCount > 0 && (
            <button onClick={markAllAsRead} className="mark-all-read-btn">
              Отметить все как прочитанные
            </button>
          )}
        </div>

        {/* Список уведомлений */}
        <div className="notifications-content">
          {loading ? (
            <div className="loading">Загрузка уведомлений...</div>
          ) : notifications.length > 0 ? (
            <div className="notifications-list">
              {notifications.map((notification) => (
                <div 
                  key={notification.id} 
                  className={`notification-item ${!notification.is_read ? 'unread' : ''}`}
                  onClick={() => handleNotificationClick(notification)}
                >
                  <div className="notification-avatar">
                    {notification.sender?.avatar ? (
                      <img 
                        src={notification.sender.avatar} 
                        alt={notification.sender.first_name || notification.sender.username}
                        onError={(e) => {
                          e.target.style.display = 'none';
                          e.target.nextSibling.style.display = 'flex';
                        }}
                      />
                    ) : null}
                    <div className="avatar-placeholder" style={{ 
                      display: notification.sender?.avatar ? 'none' : 'flex' 
                    }}>
                      {getAuthorInitials(notification.sender?.first_name || notification.sender?.username)}
                    </div>
                  </div>
                  
                  <div className="notification-content">
                    <div className="notification-icon">
                      <FontAwesomeIcon icon={getNotificationIcon(notification.type)} />
                    </div>
                    <div className="notification-text">
                      <p>{getNotificationText(notification)}</p>
                      <span className="notification-time">
                        {formatDate(notification.created_at)}
                      </span>
                    </div>
                  </div>
                  
                  {!notification.is_read && (
                    <div className="unread-indicator"></div>
                  )}
                </div>
              ))}
            </div>
          ) : (
            <div className="no-notifications">
              <div className="no-notifications-icon">
                <FontAwesomeIcon icon={faBell} />
              </div>
              <h3>Уведомлений пока нет</h3>
              <p>Когда кто-то подпишется на вас или напишет сообщение, вы увидите уведомление здесь</p>
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

